  @Test void whenExtraNamespacesAddedThenSecretsInCurrentNamespaceAreNotIncluded(){
    KubernetesConfigServerProperties kubernetesConfigServerProperties=new KubernetesConfigServerProperties();
    kubernetesConfigServerProperties.setSecretsNamespaces("team-a,team-b");
    KubernetesPropertySourceSupplier kubernetesPropertySourceSupplier=new KubernetesConfigServerAutoConfiguration().secretsPropertySourceSupplier(kubernetesConfigServerProperties);
    KubernetesEnvironmentRepository environmentRepository=new KubernetesEnvironmentRepository(coreApi,Collections.singletonList(kubernetesPropertySourceSupplier),"default");
    Environment environmentGateway=environmentRepository.findOne("gateway","","");
    assertThat(environmentGateway.getPropertySources().size()).isEqualTo(0);
    Environment environmentStores=environmentRepository.findOne("stores","","");
    assertThat(environmentStores.getPropertySources().size()).isEqualTo(1);
    Environment environmentOrders=environmentRepository.findOne("orders","","");
    assertThat(environmentOrders.getPropertySources().size()).isEqualTo(1);
  }
  private static V1ConfigMap buildConfigMap(  String name,  String namespace){
    return new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(name).withNamespace(namespace).withResourceVersion("1").build()).addToData("application.yaml","dummy:\n  property:\n    string: \"" + name + "\"\n").build();
  }
  private static V1Secret buildSecret(  String name,  String namespace){
    return new V1SecretBuilder().withMetadata(new V1ObjectMetaBuilder().withName(name).withResourceVersion("0").withNamespace(namespace).build()).addToData("password","p455w0rd".getBytes()).addToData("username","user".getBytes()).build();
  }
