@Configuration(proxyBeanMethods=false) @EnableConfigurationProperties({ConfigurationWatcherConfigurationProperties.class}) @Import({ConfigurationWatcherAutoConfiguration.RefreshTriggerConfiguration.class}) public class ConfigurationWatcherAutoConfiguration {
  private static final String AMQP="bus-amqp";
  private static final String KAFKA="bus-kafka";
  @Bean @ConditionalOnMissingBean public WebClient webClient(  WebClient.Builder webClientBuilder){
    return webClientBuilder.build();
  }
  @Bean @ConditionalOnMissingBean(ConfigMapWatcherChangeDetector.class) @ConditionalOnBean(KubernetesClientConfigMapPropertySourceLocator.class) public ConfigMapWatcherChangeDetector httpBasedConfigMapWatchChangeDetector(  AbstractEnvironment environment,  CoreV1Api coreV1Api,  KubernetesClientConfigMapPropertySourceLocator configMapPropertySourceLocator,  ConfigReloadProperties properties,  ConfigurationUpdateStrategy strategy,  ConfigurationWatcherConfigurationProperties k8SConfigurationProperties,  KubernetesNamespaceProvider namespaceProvider,  ThreadPoolTaskExecutor threadFactory,  HttpRefreshTrigger httpRefreshTrigger){
    return new HttpBasedConfigMapWatchChangeDetector(coreV1Api,environment,properties,strategy,configMapPropertySourceLocator,namespaceProvider,k8SConfigurationProperties,threadFactory,httpRefreshTrigger);
  }
  @Bean @ConditionalOnMissingBean @ConditionalOnBean(KubernetesClientSecretsPropertySourceLocator.class) public SecretsWatcherChangeDetector httpBasedSecretsWatchChangeDetector(  AbstractEnvironment environment,  CoreV1Api coreV1Api,  KubernetesClientSecretsPropertySourceLocator secretsPropertySourceLocator,  KubernetesNamespaceProvider namespaceProvider,  ConfigReloadProperties properties,  ConfigurationUpdateStrategy strategy,  ConfigurationWatcherConfigurationProperties k8SConfigurationProperties,  ThreadPoolTaskExecutor threadFactory,  HttpRefreshTrigger httpRefreshTrigger){
    return new HttpBasedSecretsWatchChangeDetector(coreV1Api,environment,properties,strategy,secretsPropertySourceLocator,namespaceProvider,k8SConfigurationProperties,threadFactory,httpRefreshTrigger);
  }
@Configuration @Profile(AMQP) @Import({ContextFunctionCatalogAutoConfiguration.class,RabbitHealthContributorAutoConfiguration.class,RefreshTriggerConfiguration.class}) static class BusRabbitConfiguration {
    @Bean @ConditionalOnMissingBean(ConfigMapWatcherChangeDetector.class) @ConditionalOnBean(KubernetesClientConfigMapPropertySourceLocator.class) public ConfigMapWatcherChangeDetector busConfigMapChangeWatcher(    AbstractEnvironment environment,    CoreV1Api coreV1Api,    KubernetesClientConfigMapPropertySourceLocator configMapPropertySourceLocator,    KubernetesNamespaceProvider kubernetesNamespaceProvider,    ConfigReloadProperties properties,    ConfigurationUpdateStrategy strategy,    ConfigurationWatcherConfigurationProperties k8SConfigurationProperties,    ThreadPoolTaskExecutor threadFactory,    BusRefreshTrigger busRefreshTrigger){
      return new BusEventBasedConfigMapWatcherChangeDetector(coreV1Api,environment,properties,strategy,configMapPropertySourceLocator,kubernetesNamespaceProvider,k8SConfigurationProperties,threadFactory,busRefreshTrigger);
    }
    @Bean @ConditionalOnMissingBean(SecretsWatcherChangeDetector.class) @ConditionalOnBean(KubernetesClientSecretsPropertySourceLocator.class) public SecretsWatcherChangeDetector busSecretsChangeWatcher(    AbstractEnvironment environment,    CoreV1Api coreV1Api,    KubernetesClientSecretsPropertySourceLocator secretsPropertySourceLocator,    ConfigReloadProperties properties,    KubernetesNamespaceProvider kubernetesNamespaceProvider,    ConfigurationUpdateStrategy strategy,    ConfigurationWatcherConfigurationProperties k8SConfigurationProperties,    ThreadPoolTaskExecutor threadFactory,    BusRefreshTrigger busRefreshTrigger){
      return new BusEventBasedSecretsWatcherChangeDetector(coreV1Api,environment,properties,strategy,secretsPropertySourceLocator,kubernetesNamespaceProvider,k8SConfigurationProperties,threadFactory,busRefreshTrigger);
    }
  }
