  @BeforeAll static void beforeAll() throws Exception {
    K3S.start();
    Commons.validateImage(SPRING_CLOUD_K8S_CONFIG_WATCHER_APP_NAME,K3S);
    Commons.loadSpringCloudKubernetesImage(SPRING_CLOUD_K8S_CONFIG_WATCHER_APP_NAME,K3S);
    util=new Util(K3S);
    util.createNamespace(LEFT_NAMESPACE);
    util.createNamespace(RIGHT_NAMESPACE);
    util.wiremock(DEFAULT_NAMESPACE,"/",Phase.CREATE);
    util.setUpClusterWide(DEFAULT_NAMESPACE,Set.of(DEFAULT_NAMESPACE,LEFT_NAMESPACE,RIGHT_NAMESPACE));
    configWatcher(Phase.CREATE);
  }
  @AfterAll static void afterAll() throws Exception {
    configWatcher(Phase.DELETE);
    util.wiremock(DEFAULT_NAMESPACE,"/",Phase.DELETE);
    util.deleteClusterWide(DEFAULT_NAMESPACE,Set.of(DEFAULT_NAMESPACE,LEFT_NAMESPACE,RIGHT_NAMESPACE));
    util.deleteNamespace(LEFT_NAMESPACE);
    util.deleteNamespace(RIGHT_NAMESPACE);
    Commons.cleanUp(SPRING_CLOUD_K8S_CONFIG_WATCHER_APP_NAME,K3S);
    Commons.systemPrune();
  }
  /** 
 * <pre> - deploy config-watcher in default namespace - deploy wiremock in default namespace (so that we could assert calls to the actuator path) - deploy configmap-left in left namespaces with proper label and "service-wiremock" name. Because of the label, this will trigger a reload; because of the name this will trigger a reload against that name. This is a http refresh against the actuator. - same as above for the configmap-right. </pre>
 */
  @Test void testConfigMapActuatorRefreshMultipleNamespaces(){
    WireMock.configureFor(WIREMOCK_HOST,WIREMOCK_PORT);
    await().timeout(Duration.ofSeconds(60)).until(() -> WireMock.stubFor(WireMock.post(WireMock.urlEqualTo("/actuator/refresh")).willReturn(WireMock.aResponse().withBody("{}").withStatus(200))).getResponse().wasConfigured());
    V1ConfigMap leftConfigMap=new V1ConfigMapBuilder().editOrNewMetadata().withLabels(Map.of("spring.cloud.kubernetes.config","true")).withName("service-wiremock").withNamespace(LEFT_NAMESPACE).endMetadata().addToData("color","purple").build();
    util.createAndWait(LEFT_NAMESPACE,leftConfigMap,null);
    V1ConfigMap rightConfigMap=new V1ConfigMapBuilder().editOrNewMetadata().withLabels(Map.of("spring.cloud.kubernetes.config","true")).withName("service-wiremock").withNamespace(RIGHT_NAMESPACE).endMetadata().addToData("color","green").build();
    util.createAndWait(RIGHT_NAMESPACE,rightConfigMap,null);
    Commons.assertReloadLogStatements("ConfigMap service-wiremock was added in namespace left","","spring-cloud-kubernetes-configuration-watcher");
    Commons.assertReloadLogStatements("ConfigMap service-wiremock was added in namespace right","","spring-cloud-kubernetes-configuration-watcher");
    await().atMost(Duration.ofSeconds(30)).until(() -> !WireMock.findAll(WireMock.postRequestedFor(WireMock.urlEqualTo("/actuator/refresh"))).isEmpty());
    WireMock.verify(WireMock.exactly(2),WireMock.postRequestedFor(WireMock.urlEqualTo("/actuator/refresh")));
    testSecretActuatorRefreshMultipleNamespaces();
  }
  /** 
 * <pre> - deploy config-watcher in default namespace - deploy wiremock in default namespace (so that we could assert calls to the actuator path) - deploy secret-left in left namespaces with proper label and "service-wiremock". Because of the label, this will trigger a reload; because of the name this will trigger a reload against that name. This is a http refresh against the actuator. - same as above for the secret-right. </pre>
 */
  void testSecretActuatorRefreshMultipleNamespaces(){
    await().timeout(Duration.ofSeconds(60)).ignoreException(SocketTimeoutException.class).until(() -> WireMock.stubFor(WireMock.post(WireMock.urlEqualTo("/actuator/refresh")).willReturn(WireMock.aResponse().withBody("{}").withStatus(200))).getResponse().wasConfigured());
    V1Secret leftSecret=new V1SecretBuilder().editOrNewMetadata().withLabels(Map.of("spring.cloud.kubernetes.secret","true")).withName("service-wiremock").withNamespace(LEFT_NAMESPACE).endMetadata().addToData("color",Base64.getEncoder().encode("purple".getBytes(StandardCharsets.UTF_8))).build();
    util.createAndWait(LEFT_NAMESPACE,null,leftSecret);
    V1Secret rightSecret=new V1SecretBuilder().editOrNewMetadata().withLabels(Map.of("spring.cloud.kubernetes.secret","true")).withName("service-wiremock").withNamespace(RIGHT_NAMESPACE).endMetadata().addToData("color",Base64.getEncoder().encode("green".getBytes(StandardCharsets.UTF_8))).build();
    util.createAndWait(RIGHT_NAMESPACE,null,rightSecret);
    Commons.assertReloadLogStatements("Secret service-wiremock was added in namespace left","","spring-cloud-kubernetes-configuration-watcher");
    Commons.assertReloadLogStatements("Secret service-wiremock was added in namespace right","","spring-cloud-kubernetes-configuration-watcher");
    await().atMost(Duration.ofSeconds(30)).until(() -> !WireMock.findAll(WireMock.postRequestedFor(WireMock.urlEqualTo("/actuator/refresh"))).isEmpty());
    WireMock.verify(WireMock.exactly(4),WireMock.postRequestedFor(WireMock.urlEqualTo("/actuator/refresh")));
  }
  private static void configWatcher(  Phase phase){
    V1ConfigMap configMap=(V1ConfigMap)util.yaml("config-watcher/spring-cloud-kubernetes-configuration-watcher-configmap.yaml");
    V1Deployment deployment=(V1Deployment)util.yaml("config-watcher/spring-cloud-kubernetes-configuration-watcher-deployment.yaml");
    List<V1EnvVar> envVars=List.of(new V1EnvVar().name("SPRING_CLOUD_KUBERNETES_RELOAD_NAMESPACES_0").value(LEFT_NAMESPACE),new V1EnvVar().name("SPRING_CLOUD_KUBERNETES_RELOAD_NAMESPACES_1").value(RIGHT_NAMESPACE),new V1EnvVar().name("LOGGING_LEVEL_ORG_SPRINGFRAMEWORK").value("TRACE"));
    deployment.getSpec().getTemplate().getSpec().getContainers().get(0).setEnv(envVars);
    V1Service service=(V1Service)util.yaml("config-watcher/spring-cloud-kubernetes-configuration-watcher-service.yaml");
    if (phase.equals(Phase.CREATE)) {
      util.createAndWait(DEFAULT_NAMESPACE,configMap,null);
      util.createAndWait(DEFAULT_NAMESPACE,null,deployment,service,null,true);
    }
 else {
      util.deleteAndWait(DEFAULT_NAMESPACE,configMap,null);
      util.deleteAndWait(DEFAULT_NAMESPACE,deployment,service,null);
    }
  }
}

