    K8sClientConfigMapReloadITUtil.patchFive(deploymentName,"default",imageName);
    Commons.waitForLogStatement("paths property sources : [/tmp/application.properties]",k3sContainer,deploymentName);
    Commons.waitForLogStatement("will add file-based property source : /tmp/application.properties",k3sContainer,deploymentName);
    WebClient webClient=K8sClientConfigMapReloadITUtil.builder().baseUrl("http://localhost/mount").build();
    String result=webClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(K8sClientConfigMapReloadITUtil.retrySpec()).block();
    Assertions.assertEquals("as-mount-initial",result);
    V1ConfigMap configMap=(V1ConfigMap)util.yaml("configmap-mount.yaml");
    configMap.setData(Map.of("application.properties","from.properties.key=as-mount-changed"));
    new CoreV1Api().replaceNamespacedConfigMap("poll-reload-as-mount","default",configMap,null,null,null,null);
    await().timeout(Duration.ofSeconds(180)).until(() -> webClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(K8sClientConfigMapReloadITUtil.retrySpec()).block().equals("as-mount-changed"));
  }
}

