    configMap=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withLabels(Map.of("new-label","abc")).withNamespace("left").withName("left-configmap").build()).withData(Map.of("left.value","left-after-change")).build();
