  /** 
 * <pre> - service "wiremock" is present in namespace "a-uat" - service "wiremock" is present in namespace "b-uat" - we search with a predicate : "#root.metadata.namespace matches '^uat.*$'" As such, both services are found via 'getInstances' call. </pre>
 */
  static void filterMatchesBothNamespacesViaThePredicate(){
    WebClient clientServices=builder().baseUrl("http://localhost/services").build();
    @SuppressWarnings("unchecked") List<String> services=(List<String>)clientServices.method(HttpMethod.GET).retrieve().bodyToMono(List.class).retryWhen(retrySpec()).block();
    Assertions.assertEquals(services.size(),1);
    Assertions.assertTrue(services.contains("service-wiremock"));
    WebClient client=builder().baseUrl("http://localhost/service-instances/service-wiremock").build();
    List<DefaultKubernetesServiceInstance> serviceInstances=client.method(HttpMethod.GET).retrieve().bodyToMono(new ParameterizedTypeReference<List<DefaultKubernetesServiceInstance>>(){
    }
).retryWhen(retrySpec()).block();
    Assertions.assertEquals(serviceInstances.size(),2);
    List<DefaultKubernetesServiceInstance> sorted=serviceInstances.stream().sorted(Comparator.comparing(DefaultKubernetesServiceInstance::getNamespace)).toList();
    DefaultKubernetesServiceInstance first=sorted.get(0);
    Assertions.assertEquals(first.getServiceId(),"service-wiremock");
    Assertions.assertNotNull(first.getInstanceId());
    Assertions.assertEquals(first.getPort(),8080);
    Assertions.assertEquals(first.getNamespace(),"a-uat");
    Assertions.assertEquals(first.getMetadata(),Map.of("app","service-wiremock","port.http","8080","k8s_namespace","a-uat","type","ClusterIP"));
    DefaultKubernetesServiceInstance second=sorted.get(1);
    Assertions.assertEquals(second.getServiceId(),"service-wiremock");
    Assertions.assertNotNull(second.getInstanceId());
    Assertions.assertEquals(second.getPort(),8080);
    Assertions.assertEquals(second.getNamespace(),"b-uat");
    Assertions.assertEquals(second.getMetadata(),Map.of("app","service-wiremock","port.http","8080","k8s_namespace","b-uat","type","ClusterIP"));
  }
  /** 
 * <pre> - service "wiremock" is present in namespace "a-uat" - service "wiremock" is present in namespace "b-uat" - we search with a predicate : "#root.metadata.namespace matches 'a-uat$'" As such, only service from 'a-uat' namespace matches. </pre>
 */
  static void filterMatchesOneNamespaceViaThePredicate(){
    WebClient clientServices=builder().baseUrl("http://localhost/services").build();
    @SuppressWarnings("unchecked") List<String> services=(List<String>)clientServices.method(HttpMethod.GET).retrieve().bodyToMono(List.class).retryWhen(retrySpec()).block();
    Assertions.assertEquals(services.size(),1);
    Assertions.assertTrue(services.contains("service-wiremock"));
    WebClient client=builder().baseUrl("http://localhost/service-instances/service-wiremock").build();
    List<DefaultKubernetesServiceInstance> serviceInstances=client.method(HttpMethod.GET).retrieve().bodyToMono(new ParameterizedTypeReference<List<DefaultKubernetesServiceInstance>>(){
    }
).retryWhen(retrySpec()).block();
    Assertions.assertEquals(serviceInstances.size(),1);
    DefaultKubernetesServiceInstance first=serviceInstances.get(0);
    Assertions.assertEquals(first.getServiceId(),"service-wiremock");
    Assertions.assertNotNull(first.getInstanceId());
    Assertions.assertEquals(first.getPort(),8080);
    Assertions.assertEquals(first.getNamespace(),"a-uat");
    Assertions.assertEquals(first.getMetadata(),Map.of("app","service-wiremock","port.http","8080","k8s_namespace","a-uat","type","ClusterIP"));
  }
}

