    util.busybox(NAMESPACE,Phase.DELETE);
    util.wiremock(NAMESPACE_A_UAT,"/wiremock",Phase.DELETE,false);
    util.wiremock(NAMESPACE_B_UAT,"/wiremock",Phase.DELETE,false);
    util.deleteNamespace(NAMESPACE_A_UAT);
    util.deleteNamespace(NAMESPACE_B_UAT);
    util.wiremock(NAMESPACE_LEFT,"/wiremock",Phase.DELETE,false);
    util.wiremock(NAMESPACE_RIGHT,"/wiremock",Phase.DELETE,false);
    util.deleteNamespace(NAMESPACE_LEFT);
    util.deleteNamespace(NAMESPACE_RIGHT);
    manifests(Phase.DELETE);
    Commons.cleanUp(IMAGE_NAME,K3S);
    Commons.systemPrune();
  }
  @Test void testPodMetadata() throws Exception {
    String[] both=K3S.execInContainer("sh","-c","kubectl get pods -l app=busybox -o=name --no-headers").getStdout().split("\n");
    K3S.execInContainer("sh","-c","kubectl label pods " + both[0].split("/")[1] + " custom-label=custom-label-value");
    K3S.execInContainer("sh","-c","kubectl annotate pods " + both[1].split("/")[1] + " custom-annotation=custom-annotation-value");
    WebClient client=builder().baseUrl("http://localhost/service-instances/busybox-service").build();
    List<DefaultKubernetesServiceInstance> serviceInstances=client.method(HttpMethod.GET).retrieve().bodyToMono(new ParameterizedTypeReference<List<DefaultKubernetesServiceInstance>>(){
    }
).retryWhen(retrySpec()).block();
    DefaultKubernetesServiceInstance withCustomLabel=serviceInstances.stream().filter(x -> x.podMetadata().getOrDefault("annotations",Map.of()).isEmpty()).toList().get(0);
    Assertions.assertEquals(withCustomLabel.getServiceId(),"busybox-service");
    Assertions.assertNotNull(withCustomLabel.getInstanceId());
    Assertions.assertNotNull(withCustomLabel.getHost());
    Assertions.assertEquals(withCustomLabel.getMetadata(),Map.of("k8s_namespace","default","type","ClusterIP","port.busybox-port","80"));
    Assertions.assertTrue(withCustomLabel.podMetadata().get("labels").entrySet().stream().anyMatch(x -> x.getKey().equals("custom-label") && x.getValue().equals("custom-label-value")));
    DefaultKubernetesServiceInstance withCustomAnnotation=serviceInstances.stream().filter(x -> !x.podMetadata().getOrDefault("annotations",Map.of()).isEmpty()).toList().get(0);
    Assertions.assertEquals(withCustomAnnotation.getServiceId(),"busybox-service");
    Assertions.assertNotNull(withCustomAnnotation.getInstanceId());
    Assertions.assertNotNull(withCustomAnnotation.getHost());
    Assertions.assertEquals(withCustomAnnotation.getMetadata(),Map.of("k8s_namespace","default","type","ClusterIP","port.busybox-port","80"));
    Assertions.assertTrue(withCustomAnnotation.podMetadata().get("annotations").entrySet().stream().anyMatch(x -> x.getKey().equals("custom-annotation") && x.getValue().equals("custom-annotation-value")));
    testAllOther();
  }
  private void testAllOther(){
    testAllServices();
    testAllServicesWithBootstrap();
    testExternalNameServiceInstance();
    testBlockingConfiguration();
    testDefaultConfiguration();
    testReactiveConfiguration();
    filterMatchesBothNamespacesViaThePredicate();
    filterMatchesOneNamespaceViaThePredicate();
    namespaceFilter();
  }
  private void testAllServices(){
    util.patchWithReplace(DOCKER_IMAGE,DEPLOYMENT_NAME,NAMESPACE,BODY_ONE,Map.of("app",IMAGE_NAME));
    Fabric8DiscoveryDelegate.testAllServices();
