final class Fabric8DiscoveryClientHealthDelegate {
  private Fabric8DiscoveryClientHealthDelegate(){
  }
  private static final String REACTIVE_STATUS="$.components.reactiveDiscoveryClients.components.['Fabric8 Kubernetes Reactive Discovery Client'].status";
  private static final String BLOCKING_STATUS="$.components.discoveryComposite.components.discoveryClient.status";
  private static final BasicJsonTester BASIC_JSON_TESTER=new BasicJsonTester(Fabric8DiscoveryClientHealthDelegate.class);
  /** 
 * Reactive is disabled, only blocking is active. As such, KubernetesInformerDiscoveryClientAutoConfiguration::indicatorInitializer will post an InstanceRegisteredEvent. We assert for logs and call '/health' endpoint to see that blocking discovery client was initialized.
 */
  static void testBlockingConfiguration(  K3sContainer k3sContainer,  String imageName){
    waitForLogStatement("Will publish InstanceRegisteredEvent from blocking implementation",k3sContainer,imageName);
    waitForLogStatement("publishing InstanceRegisteredEvent",k3sContainer,imageName);
    waitForLogStatement("Discovery Client has been initialized",k3sContainer,imageName);
    waitForLogStatement("received InstanceRegisteredEvent from pod with 'app' label value : spring-cloud-kubernetes-fabric8-client-discovery",k3sContainer,imageName);
    WebClient healthClient=builder().baseUrl("http://localhost/actuator/health").build();
    String healthResult=healthClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.discoveryComposite.status").isEqualTo("UP");
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue(BLOCKING_STATUS).isEqualTo("UP");
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathArrayValue("$.components.discoveryComposite.components.discoveryClient.details.services").containsExactlyInAnyOrder("spring-cloud-kubernetes-fabric8-client-discovery","kubernetes","busybox-service","external-name-service","service-wiremock");
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).doesNotHaveJsonPath(REACTIVE_STATUS);
  }
  /** 
 * Both blocking and reactive are enabled.
 */
  static void testDefaultConfiguration(  K3sContainer k3sContainer,  String imageName){
    waitForLogStatement("Will publish InstanceRegisteredEvent from blocking implementation",k3sContainer,imageName);
    waitForLogStatement("publishing InstanceRegisteredEvent",k3sContainer,imageName);
    waitForLogStatement("Discovery Client has been initialized",k3sContainer,imageName);
    waitForLogStatement("received InstanceRegisteredEvent from pod with 'app' label value : " + "spring-cloud-kubernetes-fabric8-client-discovery",k3sContainer,imageName);
    WebClient healthClient=builder().baseUrl("http://localhost/actuator/health").build();
    String healthResult=healthClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.discoveryComposite.status").isEqualTo("UP");
