  /** 
 * Both blocking and reactive are enabled.
 */
  void testDefaultConfiguration(  K3sContainer container){
    KubernetesClientDiscoveryClientUtils.patchForBlockingAndReactiveHealth(DEPLOYMENT_NAME,NAMESPACE);
    assertLogStatement(container,"Will publish InstanceRegisteredEvent from blocking implementation");
    assertLogStatement(container,"publishing InstanceRegisteredEvent");
    assertLogStatement(container,"Discovery Client has been initialized");
    assertLogStatement(container,"received InstanceRegisteredEvent from pod with 'app' label value : spring-cloud-kubernetes-k8s-client-discovery");
    WebClient healthClient=builder().baseUrl("http://localhost/actuator/health").build();
    WebClient infoClient=builder().baseUrl("http://localhost/actuator/info").build();
    String healthResult=healthClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
    String infoResult=infoClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.discoveryComposite.status").isEqualTo("UP");
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.discoveryComposite.components.discoveryClient.status").isEqualTo("UP");
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathArrayValue("$.components.discoveryComposite.components.discoveryClient.details.services").containsExactlyInAnyOrder("spring-cloud-kubernetes-k8s-client-discovery","kubernetes","external-name-service");
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.reactiveDiscoveryClients.status").isEqualTo("UP");
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.reactiveDiscoveryClients.components.['Kubernetes Reactive Discovery Client'].status").isEqualTo("UP");
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathArrayValue("$.components.reactiveDiscoveryClients.components.['Kubernetes Reactive Discovery Client'].details.services").containsExactlyInAnyOrder("spring-cloud-kubernetes-k8s-client-discovery","kubernetes","external-name-service");
    assertHealth(healthResult);
    assertInfo(infoResult);
  }
  private void assertHealth(  String healthResult){
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.kubernetes.status").isEqualTo("UP");
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.kubernetes.details.hostIp").isNotEmpty();
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathBooleanValue("$.components.kubernetes.details.inside").isEqualTo(true);
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.kubernetes.details.labels.app").isEqualTo("spring-cloud-kubernetes-k8s-client-discovery");
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.kubernetes.details.namespace").isNotEmpty();
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.kubernetes.details.nodeName").isNotEmpty();
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.kubernetes.details.podIp").isNotEmpty();
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.kubernetes.details.podName").isNotEmpty();
    Assertions.assertThat(BASIC_JSON_TESTER.from(healthResult)).extractingJsonPathStringValue("$.components.kubernetes.details.serviceAccount").isNotEmpty();
  }
  private void assertInfo(  String infoResult){
    Assertions.assertThat(BASIC_JSON_TESTER.from(infoResult)).extractingJsonPathStringValue("$.kubernetes.hostIp").isNotEmpty();
    Assertions.assertThat(BASIC_JSON_TESTER.from(infoResult)).extractingJsonPathBooleanValue("$.kubernetes.inside").isEqualTo(true);
    Assertions.assertThat(BASIC_JSON_TESTER.from(infoResult)).extractingJsonPathStringValue("$.kubernetes.namespace").isNotEmpty();
    Assertions.assertThat(BASIC_JSON_TESTER.from(infoResult)).extractingJsonPathStringValue("$.kubernetes.nodeName").isNotEmpty();
    Assertions.assertThat(BASIC_JSON_TESTER.from(infoResult)).extractingJsonPathStringValue("$.kubernetes.podIp").isNotEmpty();
    Assertions.assertThat(BASIC_JSON_TESTER.from(infoResult)).extractingJsonPathStringValue("$.kubernetes.podName").isNotEmpty();
    Assertions.assertThat(BASIC_JSON_TESTER.from(infoResult)).extractingJsonPathStringValue("$.kubernetes.serviceAccount").isNotEmpty();
  }
  private WebClient.Builder builder(){
    return WebClient.builder().clientConnector(new ReactorClientHttpConnector(HttpClient.create()));
  }
  private RetryBackoffSpec retrySpec(){
    return Retry.fixedDelay(15,Duration.ofSeconds(1)).filter(Objects::nonNull);
  }
  private void assertLogStatement(  K3sContainer container,  String message){
    try {
      String appPodName=container.execInContainer("sh","-c","kubectl get pods -l app=" + DEPLOYMENT_NAME + " -o=name --no-headers | tr -d '\n'").getStdout();
      await().pollDelay(Duration.ofSeconds(4)).pollInterval(Duration.ofSeconds(1)).atMost(20,TimeUnit.SECONDS).until(() -> {
        Container.ExecResult execResult=container.execInContainer("sh","-c","kubectl logs " + appPodName.trim());
        String ok=execResult.getStdout();
        return ok.contains(message);
      }
);
    }
 catch (    Exception e) {
      e.printStackTrace();
      throw new RuntimeException(e);
    }
  }
}

