    util.deleteNamespace(NAMESPACE_A_UAT);
    util.deleteNamespace(NAMESPACE_B_UAT);
  }
  private static void manifests(  Phase phase){
    V1Deployment deployment=(V1Deployment)util.yaml("kubernetes-discovery-deployment.yaml");
    V1Service service=(V1Service)util.yaml("kubernetes-discovery-service.yaml");
    V1Service externalNameService=(V1Service)util.yaml("external-name-service.yaml");
    V1Ingress ingress=(V1Ingress)util.yaml("kubernetes-discovery-ingress.yaml");
    if (phase.equals(Phase.DELETE)) {
      util.deleteAndWait(NAMESPACE,deployment,service,ingress);
      util.deleteAndWait(NAMESPACE,null,externalNameService,null);
      return;
    }
    if (phase.equals(Phase.CREATE)) {
      List<V1EnvVar> envVars=new ArrayList<>(Optional.ofNullable(deployment.getSpec().getTemplate().getSpec().getContainers().get(0).getEnv()).orElse(List.of()));
      V1EnvVar debugLevel=new V1EnvVar().name("LOGGING_LEVEL_ORG_SPRINGFRAMEWORK_CLOUD_KUBERNETES_CLIENT_DISCOVERY").value("DEBUG");
      V1EnvVar commonsLevel=new V1EnvVar().name("LOGGING_LEVEL_ORG_SPRINGFRAMEWORK_CLOUD_KUBERNETES_COMMONS_DISCOVERY").value("DEBUG");
      V1EnvVar debugLevelForClient=new V1EnvVar().name("LOGGING_LEVEL_ORG_SPRINGFRAMEWORK_CLOUD_KUBERNETES_CLIENT").value("DEBUG");
      V1EnvVar addLabels=new V1EnvVar().name("SPRING_CLOUD_KUBERNETES_DISCOVERY_METADATA_ADDPODLABELS").value("TRUE");
      V1EnvVar addAnnotations=new V1EnvVar().name("SPRING_CLOUD_KUBERNETES_DISCOVERY_METADATA_ADDPODANNOTATIONS").value("TRUE");
      envVars.add(debugLevel);
      envVars.add(debugLevelForClient);
      envVars.add(addLabels);
      envVars.add(addAnnotations);
      envVars.add(commonsLevel);
      deployment.getSpec().getTemplate().getSpec().getContainers().get(0).setEnv(envVars);
      util.createAndWait(NAMESPACE,null,deployment,service,ingress,true);
      util.createAndWait(NAMESPACE,null,null,externalNameService,null,true);
    }
  }
  private WebClient.Builder builder(){
    return WebClient.builder().clientConnector(new ReactorClientHttpConnector(HttpClient.create()));
  }
  private RetryBackoffSpec retrySpec(){
    return Retry.fixedDelay(15,Duration.ofSeconds(1)).filter(Objects::nonNull);
  }
  private void assertServicePresentInNamespaces(  List<String> namespaces,  String value,  String serviceName){
    namespaces.forEach(x -> {
      try {
        String service=K3S.execInContainer("sh","-c","kubectl get services -n " + x + " -l app="+ value+ " -o=name --no-headers | tr -d '\n'").getStdout();
        Assertions.assertEquals(service,"service/" + serviceName);
      }
 catch (      Exception e) {
        throw new RuntimeException(e);
      }
    }
);
  }
}

