  private static Util util;
  private static final K3sContainer K3S=Commons.container();
  @BeforeAll static void beforeAll() throws Exception {
    K3S.start();
    Commons.validateImage(IMAGE_NAME,K3S);
    Commons.loadSpringCloudKubernetesImage(IMAGE_NAME,K3S);
    Images.loadWiremock(K3S);
    util=new Util(K3S);
    util.createNamespace(NAMESPACE_A);
    util.createNamespace(NAMESPACE_B);
    util.setUpClusterWide(NAMESPACE,Set.of(NAMESPACE,NAMESPACE_A,NAMESPACE_B));
    util.wiremock(NAMESPACE,"/wiremock",Phase.CREATE,false);
    util.wiremock(NAMESPACE_A,"/wiremock",Phase.CREATE,false);
    util.wiremock(NAMESPACE_B,"/wiremock",Phase.CREATE,false);
    manifests(Phase.CREATE);
  }
  @AfterAll static void afterAll() throws Exception {
    Commons.cleanUp(IMAGE_NAME,K3S);
    util.wiremock(NAMESPACE,"/wiremock",Phase.DELETE,false);
    util.wiremock(NAMESPACE_A,"/wiremock",Phase.DELETE,false);
    util.wiremock(NAMESPACE_B,"/wiremock",Phase.DELETE,false);
    util.deleteClusterWide(NAMESPACE,Set.of(NAMESPACE,NAMESPACE_A,NAMESPACE_B));
    util.deleteNamespace(NAMESPACE_A);
    util.deleteNamespace(NAMESPACE_B);
    manifests(Phase.DELETE);
    Commons.systemPrune();
  }
  /** 
 * Deploy wiremock in 3 namespaces: default, a, b. Search only in selective namespace 'a' with blocking enabled and reactive disabled, as such find a single service and its service instance.
 */
  @Test @Order(1) void testOneNamespaceBlockingOnly(){
    Commons.waitForLogStatement("using selective namespaces : [a]",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("ConditionalOnSelectiveNamespacesMissing : found selective namespaces : [a]",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("ConditionalOnSelectiveNamespacesPresent : found selective namespaces : [a]",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("registering lister (for services) in namespace : a",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("registering lister (for endpoints) in namespace : a",K3S,IMAGE_NAME);
    Commons.waitForLogStatement(BLOCKING_PUBLISH,K3S,IMAGE_NAME);
    Assertions.assertFalse(logs().contains(REACTIVE_PUBLISH));
    blockingCheck();
  }
  /** 
 * Deploy wiremock in 3 namespaces: default, a, b. Search only in selective namespace 'a' with blocking disabled and reactive enabled, as such find a single service and its service instance.
 */
  @Test @Order(2) void testOneNamespaceReactiveOnly(){
    KubernetesClientDiscoveryClientUtils.patchForReactiveOnly(DEPLOYMENT_NAME,NAMESPACE);
    Commons.waitForLogStatement("using selective namespaces : [a]",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("ConditionalOnSelectiveNamespacesMissing : found selective namespaces : [a]",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("registering lister (for services) in namespace : a",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("registering lister (for endpoints) in namespace : a",K3S,IMAGE_NAME);
    Commons.waitForLogStatement(REACTIVE_PUBLISH,K3S,IMAGE_NAME);
    Assertions.assertFalse(logs().contains(BLOCKING_PUBLISH));
    reactiveCheck();
  }
  /** 
 * Deploy wiremock in 3 namespaces: default, a, b. Search only in selective namespace 'a' with blocking enabled and reactive enabled, as such find a single service and its service instance.
 */
  @Test @Order(3) void testOneNamespaceBothBlockingAndReactive(){
    KubernetesClientDiscoveryClientUtils.patchForBlockingAndReactive(DEPLOYMENT_NAME,NAMESPACE);
    Commons.waitForLogStatement("using selective namespaces : [a]",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("ConditionalOnSelectiveNamespacesMissing : found selective namespaces : [a]",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("ConditionalOnSelectiveNamespacesPresent : found selective namespaces : [a]",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("registering lister (for services) in namespace : a",K3S,IMAGE_NAME);
    Commons.waitForLogStatement("registering lister (for endpoints) in namespace : a",K3S,IMAGE_NAME);
    Commons.waitForLogStatement(BLOCKING_PUBLISH,K3S,IMAGE_NAME);
    Commons.waitForLogStatement(REACTIVE_PUBLISH,K3S,IMAGE_NAME);
    blockingCheck();
    reactiveCheck();
  }
  /** 
 * previous test already has: <pre> - SPRING_CLOUD_KUBERNETES_DISCOVERY_NAMESPACES_0 = a - SPRING_CLOUD_DISCOVERY_REACTIVE_ENABLED = TRUE - SPRING_CLOUD_DISCOVERY_BLOCKING_ENABLED = TRUE All we need to patch for is: -  add one more namespace to track, via SPRING_CLOUD_KUBERNETES_DISCOVERY_NAMESPACES_1 = b - disable reactive, via SPRING_CLOUD_DISCOVERY_REACTIVE_ENABLED = FALSE As such, two namespaces + blocking only, is achieved. </pre>
 */
  @Test @Order(4) void testTwoNamespacesBlockingOnly(){
    KubernetesClientDiscoveryClientUtils.patchForTwoNamespacesBlockingOnly(DEPLOYMENT_NAME,NAMESPACE);
    new KubernetesClientDiscoveryMultipleSelectiveNamespacesITDelegate().testTwoNamespacesBlockingOnly(K3S);
  }
  /** 
 * previous test already has: <pre> - SPRING_CLOUD_KUBERNETES_DISCOVERY_NAMESPACES_0 = a - SPRING_CLOUD_KUBERNETES_DISCOVERY_NAMESPACES_1 = b - SPRING_CLOUD_DISCOVERY_REACTIVE_ENABLED = FALSE - SPRING_CLOUD_DISCOVERY_BLOCKING_ENABLED = TRUE We invert the reactive and blocking in this test via patching. As such, two namespaces + reactive only, is achieved. </pre>
 */
  @Test @Order(5) void testTwoNamespacesReactiveOnly(){
    KubernetesClientDiscoveryClientUtils.patchForReactiveOnly(DEPLOYMENT_NAME,NAMESPACE);
    new KubernetesClientDiscoveryMultipleSelectiveNamespacesITDelegate().testTwoNamespaceReactiveOnly(K3S);
  }
  /** 
 * previous test already has: <pre> - SPRING_CLOUD_KUBERNETES_DISCOVERY_NAMESPACES_0 = a - SPRING_CLOUD_KUBERNETES_DISCOVERY_NAMESPACES_1 = b - SPRING_CLOUD_DISCOVERY_REACTIVE_ENABLED = TRUE - SPRING_CLOUD_DISCOVERY_BLOCKING_ENABLED = FALSE We invert the blocking support. As such, two namespaces + blocking and reactive, is achieved. </pre>
 */
