    KubernetesClientDiscoveryClientUtils.patchToAddBlockingSupport(DEPLOYMENT_NAME,NAMESPACE);
    new KubernetesClientDiscoveryMultipleSelectiveNamespacesITDelegate().testTwoNamespacesBothBlockingAndReactive(K3S);
  }
  private static void manifests(  Phase phase){
    V1Deployment deployment=(V1Deployment)util.yaml("kubernetes-discovery-deployment.yaml");
    V1Service service=(V1Service)util.yaml("kubernetes-discovery-service.yaml");
    V1Ingress ingress=(V1Ingress)util.yaml("kubernetes-discovery-ingress.yaml");
    if (phase.equals(Phase.DELETE)) {
      util.deleteAndWait(NAMESPACE,deployment,service,ingress);
      return;
    }
    if (phase.equals(Phase.CREATE)) {
      List<V1EnvVar> envVars=new ArrayList<>(Optional.ofNullable(deployment.getSpec().getTemplate().getSpec().getContainers().get(0).getEnv()).orElse(List.of()));
      V1EnvVar debugLevel=new V1EnvVar().name("LOGGING_LEVEL_ORG_SPRINGFRAMEWORK_CLOUD_KUBERNETES_CLIENT_DISCOVERY").value("DEBUG");
      V1EnvVar selectiveNamespaceA=new V1EnvVar().name("SPRING_CLOUD_KUBERNETES_DISCOVERY_NAMESPACES_0").value(NAMESPACE_A);
      V1EnvVar disableReactiveEnvVar=new V1EnvVar().name("SPRING_CLOUD_DISCOVERY_REACTIVE_ENABLED").value("FALSE");
      envVars.add(disableReactiveEnvVar);
      envVars.add(debugLevel);
      envVars.add(selectiveNamespaceA);
      deployment.getSpec().getTemplate().getSpec().getContainers().get(0).setEnv(envVars);
      util.createAndWait(NAMESPACE,null,deployment,service,ingress,true);
    }
  }
  private void reactiveCheck(){
    WebClient servicesClient=builder().baseUrl("http://localhost/reactive/services").build();
    List<String> servicesResult=servicesClient.method(HttpMethod.GET).retrieve().bodyToMono(new ParameterizedTypeReference<List<String>>(){
    }
).retryWhen(retrySpec()).block();
    Assertions.assertEquals(servicesResult.size(),1);
    Assertions.assertTrue(servicesResult.contains("service-wiremock"));
    WebClient ourServiceClient=builder().baseUrl("http://localhost/reactive/service-instances/service-wiremock").build();
    List<DefaultKubernetesServiceInstance> ourServiceInstances=ourServiceClient.method(HttpMethod.GET).retrieve().bodyToMono(new ParameterizedTypeReference<List<DefaultKubernetesServiceInstance>>(){
    }
).retryWhen(retrySpec()).block();
    Assertions.assertEquals(ourServiceInstances.size(),1);
    DefaultKubernetesServiceInstance serviceInstance=ourServiceInstances.get(0);
    Assertions.assertEquals(serviceInstance.getNamespace(),"a");
  }
  private void blockingCheck(){
    WebClient servicesClient=builder().baseUrl("http://localhost/services").build();
    List<String> servicesResult=servicesClient.method(HttpMethod.GET).retrieve().bodyToMono(new ParameterizedTypeReference<List<String>>(){
    }
).retryWhen(retrySpec()).block();
    Assertions.assertEquals(servicesResult.size(),1);
    Assertions.assertTrue(servicesResult.contains("service-wiremock"));
    WebClient ourServiceClient=builder().baseUrl("http://localhost/service-instances/service-wiremock").build();
    List<DefaultKubernetesServiceInstance> ourServiceInstances=ourServiceClient.method(HttpMethod.GET).retrieve().bodyToMono(new ParameterizedTypeReference<List<DefaultKubernetesServiceInstance>>(){
    }
).retryWhen(retrySpec()).block();
    Assertions.assertEquals(ourServiceInstances.size(),1);
    DefaultKubernetesServiceInstance serviceInstance=ourServiceInstances.get(0);
    Assertions.assertEquals(serviceInstance.getNamespace(),"a");
  }
  private String logs(){
    try {
      String appPodName=K3S.execInContainer("sh","-c","kubectl get pods -l app=" + IMAGE_NAME + " -o=name --no-headers | tr -d '\n'").getStdout();
      Container.ExecResult execResult=K3S.execInContainer("sh","-c","kubectl logs " + appPodName.trim());
      return execResult.getStdout();
    }
 catch (    Exception e) {
      e.printStackTrace();
      throw new RuntimeException(e);
    }
  }
  private WebClient.Builder builder(){
    return WebClient.builder().clientConnector(new ReactorClientHttpConnector(HttpClient.create()));
  }
  private RetryBackoffSpec retrySpec(){
    return Retry.fixedDelay(15,Duration.ofSeconds(1)).filter(Objects::nonNull);
  }
}

