final class DiscoveryClientFilterNamespaceDelegate {
  private DiscoveryClientFilterNamespaceDelegate(){
  }
  private static final BasicJsonTester BASIC_JSON_TESTER=new BasicJsonTester(DiscoveryClientFilterNamespaceDelegate.class);
  static void testNamespaceDiscoveryClient(  K3sContainer container){
    testLoadBalancer();
    testHealth();
    testForHeartbeat(container);
  }
  private static void testLoadBalancer(){
    WebClient.Builder builder=builder();
    WebClient serviceClient=builder.baseUrl("http://localhost:80/discoveryclient-it/services").build();
    String result=serviceClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
    Assertions.assertThat(BASIC_JSON_TESTER.from(result)).extractingJsonPathArrayValue("$").contains("service-wiremock");
    WebClient serviceInstanceClient=builder.baseUrl("http://localhost:80/discoveryclient-it/service/service-wiremock").build();
    String serviceInstances=serviceInstanceClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
    Assertions.assertThat(BASIC_JSON_TESTER.from(serviceInstances)).extractingJsonPathStringValue("$.[0].serviceId").isEqualTo("service-wiremock");
    Assertions.assertThat(BASIC_JSON_TESTER.from(serviceInstances)).extractingJsonPathStringValue("$.[0].namespace").isEqualTo("left");
  }
  private static void testHealth(){
    WebClient.Builder builder=builder();
    WebClient serviceClient=builder.baseUrl("http://localhost:80/discoveryclient-it/actuator/health").build();
    String health=serviceClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
    Assertions.assertThat(BASIC_JSON_TESTER.from(health)).extractingJsonPathStringValue("$.components.discoveryComposite.status").isEqualTo("UP");
  }
  private static void testForHeartbeat(  K3sContainer container){
    Commons.waitForLogStatement("using delay : 3000",container,"spring-cloud-kubernetes-discoveryserver");
    Commons.waitForLogStatement("received heartbeat event",container,"spring-cloud-kubernetes-discoveryserver");
    Commons.waitForLogStatement("state received :",container,"spring-cloud-kubernetes-discoveryserver");
    Commons.waitForLogStatement("using delay : 3000",container,"spring-cloud-kubernetes-k8s-client-discovery-server");
    Commons.waitForLogStatement("state received : ",container,"spring-cloud-kubernetes-k8s-client-discovery-server");
    WebClient.Builder builder=builder();
    WebClient client=builder.baseUrl("http://localhost:80/discoveryclient-it/state").build();
    String result=client.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
    Condition<LinkedHashMap<String,String>> wireMockService=new Condition<>(map -> map.entrySet().stream().anyMatch(en -> en.getValue().contains("service-wiremock-deployment")),"");
    Condition<LinkedHashMap<String,String>> discoveryServerService=new Condition<>(map -> map.entrySet().stream().anyMatch(en -> en.getValue().contains("spring-cloud-kubernetes-k8s-client-discovery-server")),"");
    Assertions.assertThat(BASIC_JSON_TESTER.from(result)).<LinkedHashMap<String,String>>extractingJsonPathArrayValue("$.[*]").areAtLeastOne(wireMockService);
    Assertions.assertThat(BASIC_JSON_TESTER.from(result)).<LinkedHashMap<String,String>>extractingJsonPathArrayValue("$.[*]").areAtLeastOne(discoveryServerService);
  }
  private static WebClient.Builder builder(){
    return WebClient.builder().clientConnector(new ReactorClientHttpConnector(HttpClient.create()));
  }
  private static RetryBackoffSpec retrySpec(){
    return Retry.fixedDelay(15,Duration.ofSeconds(1)).filter(Objects::nonNull);
  }
}

