  @Test void testRefresh(){
    V1Secret secret=new V1SecretBuilder().editOrNewMetadata().withName(SECRET_NAME).addToLabels("spring.cloud.kubernetes.secret","true").addToAnnotations("spring.cloud.kubernetes.secret.apps","spring-cloud-kubernetes-client-configuration-watcher-secret-app-a, " + "spring-cloud-kubernetes-client-configuration-watcher-secret-app-b").endMetadata().build();
