final class BootstrapEnabledPollingReloadConfigMapMountDelegate {
  /** 
 * <pre> - we have bootstrap enabled, which means we will 'locate' property sources from config maps. - there are no explicit config maps to search for, but what we will also read, is 'spring.cloud.kubernetes.config.paths', which we have set to '/tmp/application.properties' in this test. That is populated by the volumeMounts (see deployment-mount.yaml) - we first assert that we are actually reading the path based source via (1), (2) and (3). - we then change the config map content, wait for k8s to pick it up and replace them - our polling will then detect that change, and trigger a reload. </pre>
 */
  static void testPollingReloadConfigMapWithBootstrap(  KubernetesClient client,  Util util,  K3sContainer container,  String appLabelValue){
    Commons.waitForLogStatement("paths property sources : [/tmp/application.properties]",container,appLabelValue);
    Commons.waitForLogStatement("will add file-based property source : /tmp/application.properties",container,appLabelValue);
    WebClient webClient=TestUtil.builder().baseUrl("http://localhost/key").build();
    String result=webClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(TestUtil.retrySpec()).block();
    Assertions.assertEquals("as-mount-initial",result);
    InputStream configMapStream=util.inputStream("configmap.yaml");
    ConfigMap configMap=Serialization.unmarshal(configMapStream,ConfigMap.class);
    configMap.setData(Map.of("application.properties","from.properties.key=as-mount-changed"));
    client.configMaps().inNamespace("default").resource(configMap).createOrReplace();
    await().timeout(Duration.ofSeconds(360)).until(() -> webClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(TestUtil.retrySpec()).block().equals("as-mount-changed"));
  }
}

