    util=new Util(K3S);
    client=util.client();
    util.createNamespace("left");
    util.createNamespace("right");
    util.setUpClusterWide(NAMESPACE,Set.of("left","right"));
    util.setUp(NAMESPACE);
    manifests(Phase.CREATE);
  }
  @AfterAll static void afterAll() throws Exception {
    util.deleteNamespace("left");
    util.deleteNamespace("right");
    Commons.cleanUp(IMAGE_NAME,K3S);
    Commons.systemPrune();
    manifests(Phase.DELETE);
  }
  /** 
 * <pre> - there are two namespaces : left and right - each of the namespaces has one configmap - we watch the "left" namespace, but make a change in the configmap in the right namespace - as such, no event is triggered and "left-configmap" stays as-is </pre>
 */
  @Test void testInformFromOneNamespaceEventNotTriggered(){
    Commons.assertReloadLogStatements("added configmap informer for namespace","added secret informer for namespace",IMAGE_NAME);
    WebClient webClient=TestUtil.builder().baseUrl("http://localhost/left").build();
    String result=webClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(TestUtil.retrySpec()).block();
    Assertions.assertEquals("left-initial",result);
    webClient=TestUtil.builder().baseUrl("http://localhost/right").build();
    result=webClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(TestUtil.retrySpec()).block();
    Assertions.assertEquals("right-initial",result);
    ConfigMap rightConfigMapAfterChange=new ConfigMapBuilder().withMetadata(new ObjectMetaBuilder().withNamespace("right").withName("right-configmap").build()).withData(Map.of("right.value","right-after-change")).build();
    TestUtil.replaceConfigMap(client,rightConfigMapAfterChange,"right");
    webClient=TestUtil.builder().baseUrl("http://localhost/left").build();
    WebClient finalWebClient=webClient;
    await().pollInterval(Duration.ofSeconds(1)).atMost(Duration.ofSeconds(30)).until(() -> {
      String innerResult=finalWebClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(TestUtil.retrySpec()).block();
      return "left-initial".equals(innerResult);
    }
);
    testInformFromOneNamespaceEventTriggered();
    testInform();
    testInformFromOneNamespaceEventTriggeredSecretsDisabled();
    testDataChangesInConfigMap();
    testConfigMapMountPollingReload();
    testPollingReloadConfigMapWithBootstrap();
    testSecretReload();
  }
  /** 
 * <pre> - there are two namespaces : left and right - each of the namespaces has one configmap - we watch the "right" namespace and make a change in the configmap in the same namespace - as such, event is triggered and we see the updated value </pre>
 */
  void testInformFromOneNamespaceEventTriggered(){
    TestUtil.reCreateSources(util,client);
    TestUtil.patchOne(util,DOCKER_IMAGE,IMAGE_NAME,NAMESPACE);
    Commons.assertReloadLogStatements("added configmap informer for namespace","added secret informer for namespace",IMAGE_NAME);
    WebClient webClient=TestUtil.builder().baseUrl("http://localhost/right").build();
    String result=webClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(TestUtil.retrySpec()).block();
    Assertions.assertEquals("right-initial",result);
    ConfigMap rightConfigMapAfterChange=new ConfigMapBuilder().withMetadata(new ObjectMetaBuilder().withNamespace("right").withName("right-configmap").build()).withData(Map.of("right.value","right-after-change")).build();
    TestUtil.replaceConfigMap(client,rightConfigMapAfterChange,"right");
    String[] resultAfterChange=new String[1];
    await().pollInterval(Duration.ofSeconds(3)).atMost(Duration.ofSeconds(90)).until(() -> {
      WebClient innerWebClient=TestUtil.builder().baseUrl("http://localhost/right").build();
      String innerResult=innerWebClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(TestUtil.retrySpec()).block();
      resultAfterChange[0]=innerResult;
      return innerResult != null;
    }
);
    Assertions.assertEquals("right-after-change",resultAfterChange[0]);
  }
  /** 
 * <pre> - there are two namespaces : left and right (though we do not care about the left one) - left has one configmap : left-configmap - right has two configmaps: right-configmap, right-configmap-with-label - we watch the "right" namespace, but enable tagging; which means that only right-configmap-with-label triggers changes. </pre>
 */
  void testInform(){
    TestUtil.reCreateSources(util,client);
    TestUtil.patchTwo(util,DOCKER_IMAGE,IMAGE_NAME,NAMESPACE);
    Commons.assertReloadLogStatements("added configmap informer for namespace","added secret informer for namespace",IMAGE_NAME);
    WebClient rightWebClient=TestUtil.builder().baseUrl("http://localhost/right").build();
