    TestUtil.replaceConfigMap(client,rightConfigMapAfterChange,"right");
    await().pollInterval(Duration.ofSeconds(1)).atMost(Duration.ofSeconds(30)).until(() -> {
      String innerRightResult=rightWebClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(TestUtil.retrySpec()).block();
      return "right-initial".equals(innerRightResult);
    }
);
    ConfigMap rightWithLabelConfigMapAfterChange=new ConfigMapBuilder().withMetadata(new ObjectMetaBuilder().withNamespace("right").withName("right-configmap-with-label").build()).withData(Map.of("right.with.label.value","right-with-label-after-change")).build();
    TestUtil.replaceConfigMap(client,rightWithLabelConfigMapAfterChange,"right");
    String[] resultAfterChange=new String[1];
    await().pollInterval(Duration.ofSeconds(3)).atMost(Duration.ofSeconds(90)).until(() -> {
      WebClient innerWebClient=TestUtil.builder().baseUrl("http://localhost/with-label").build();
      String innerResult=innerWebClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(TestUtil.retrySpec()).block();
      resultAfterChange[0]=innerResult;
      return innerResult != null;
    }
);
    Assertions.assertEquals("right-with-label-after-change",resultAfterChange[0]);
    rightResult=rightWebClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(TestUtil.retrySpec()).block();
    Assertions.assertEquals("right-after-change",rightResult);
  }
  /** 
 * <pre> - there are two namespaces : left and right - each of the namespaces has one configmap - secrets are disabled - we watch the "right" namespace and make a change in the configmap in the same namespace - as such, event is triggered and we see the updated value </pre>
