final class DataChangesInConfigMapReloadDelegate {
  private static final String IMAGE_NAME="spring-cloud-kubernetes-fabric8-client-reload";
  private static final String LEFT_NAMESPACE="left";
  /** 
 * <pre> - configMap with no labels and data: left.value = left-initial exists in namespace left - we assert that we can read it correctly first, by invoking localhost/left - then we change the configmap by adding a label, this in turn does not change the result of localhost/left, because the data has not changed. - then we change data inside the config map, and we must see the updated value </pre>
 */
  static void testDataChangesInConfigMap(  KubernetesClient client,  K3sContainer container,  String appLabelValue){
    Commons.assertReloadLogStatements("added configmap informer for namespace","added secret informer for namespace",IMAGE_NAME);
    WebClient webClient=builder().baseUrl("http://localhost/" + LEFT_NAMESPACE).build();
    String result=webClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
    Assertions.assertEquals("left-initial",result);
    ConfigMap configMap=new ConfigMapBuilder().withMetadata(new ObjectMetaBuilder().withLabels(Map.of("new-label","abc")).withNamespace("left").withName("left-configmap").build()).withData(Map.of("left.value","left-initial")).build();
    replaceConfigMap(client,configMap,"left");
    await().pollInterval(Duration.ofSeconds(3)).atMost(Duration.ofSeconds(90)).until(() -> {
      WebClient innerWebClient=builder().baseUrl("http://localhost/" + LEFT_NAMESPACE).build();
      String innerResult=innerWebClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
      return "left-initial".equals(innerResult);
    }
);
    String logs=logs(container,appLabelValue);
    Assertions.assertTrue(logs.contains("ConfigMap left-configmap was updated in namespace left"));
    Assertions.assertTrue(logs.contains("data in configmap has not changed, will not reload"));
    configMap=new ConfigMapBuilder().withMetadata(new ObjectMetaBuilder().withLabels(Map.of("new-label","abc")).withNamespace("left").withName("left-configmap").build()).withData(Map.of("left.value","left-after-change")).build();
    replaceConfigMap(client,configMap,"left");
    await().pollInterval(Duration.ofSeconds(3)).atMost(Duration.ofSeconds(90)).until(() -> {
      WebClient innerWebClient=builder().baseUrl("http://localhost/" + LEFT_NAMESPACE).build();
      String innerResult=innerWebClient.method(HttpMethod.GET).retrieve().bodyToMono(String.class).retryWhen(retrySpec()).block();
      return "left-after-change".equals(innerResult);
    }
);
  }
}

