        configMapPropertySourceLocator=new ConfigDataRetryableConfigMapPropertySourceLocator(configMapPropertySourceLocator,configMapProperties,new KubernetesClientConfigMapsCache());
      }
      registerSingle(bootstrapContext,ConfigMapPropertySourceLocator.class,configMapPropertySourceLocator,"configDataConfigMapPropertySourceLocator");
    }
    if (secretsProperties != null && secretsProperties.enabled()) {
      SecretsPropertySourceLocator secretsPropertySourceLocator=new KubernetesClientSecretsPropertySourceLocator(coreV1Api,namespaceProvider,secretsProperties);
      if (isRetryEnabledForSecrets(secretsProperties)) {
        secretsPropertySourceLocator=new ConfigDataRetryableSecretsPropertySourceLocator(secretsPropertySourceLocator,secretsProperties,new KubernetesClientSecretsCache());
