public final class KubernetesClientConfigUtils {
  private static final Log LOG=LogFactory.getLog(KubernetesClientConfigUtils.class);
  private static final boolean DECODE=Boolean.FALSE;
  private KubernetesClientConfigUtils(){
  }
  /** 
 * finds namespaces to be used for the event based reloading.
 */
  public static Set<String> namespaces(  KubernetesNamespaceProvider provider,  ConfigReloadProperties properties,  String target){
    Set<String> namespaces=properties.namespaces();
    if (namespaces.isEmpty()) {
      namespaces=Set.of(getApplicationNamespace(null,target,provider));
    }
    LOG.debug("informer namespaces : " + namespaces);
    return namespaces;
  }
  /** 
 * <pre> 1. read all secrets in the provided namespace 2. from the above, filter the ones that we care about (filter by labels) 3. with secret names from (2), find out if there are any profile based secrets (if profiles is not empty) 4. concat (2) and (3) and these are the secrets we are interested in 5. see if any of the secrets from (4) has a single yaml/properties file 6. gather all the names of the secrets (from 4) + data they hold </pre>
 */
  static MultipleSourcesContainer secretsDataByLabels(  CoreV1Api coreV1Api,  String namespace,  Map<String,String> labels,  Environment environment,  Set<String> profiles){
    List<StrippedSourceContainer> strippedSecrets=strippedSecrets(coreV1Api,namespace);
    if (strippedSecrets.isEmpty()) {
      return MultipleSourcesContainer.empty();
    }
    return ConfigUtils.processLabeledData(strippedSecrets,environment,labels,namespace,profiles,DECODE);
  }
  /** 
 * <pre> 1. read all config maps in the provided namespace 2. from the above, filter the ones that we care about (filter by labels) 3. with config maps names from (2), find out if there are any profile based ones (if profiles is not empty) 4. concat (2) and (3) and these are the config maps we are interested in 5. see if any from (4) has a single yaml/properties file 6. gather all the names of the config maps (from 4) + data they hold </pre>
 */
  static MultipleSourcesContainer configMapsDataByLabels(  CoreV1Api coreV1Api,  String namespace,  Map<String,String> labels,  Environment environment,  Set<String> profiles){
    List<StrippedSourceContainer> strippedConfigMaps=strippedConfigMaps(coreV1Api,namespace);
    if (strippedConfigMaps.isEmpty()) {
      return MultipleSourcesContainer.empty();
    }
    return ConfigUtils.processLabeledData(strippedConfigMaps,environment,labels,namespace,profiles,DECODE);
  }
  /** 
 * <pre> 1. read all secrets in the provided namespace 2. from the above, filter the ones that we care about (by name) 3. see if any of the secrets has a single yaml/properties file 4. gather all the names of the secrets + decoded data they hold </pre>
 */
  static MultipleSourcesContainer secretsDataByName(  CoreV1Api coreV1Api,  String namespace,  LinkedHashSet<String> sourceNames,  Environment environment,  boolean includeDefaultProfileData){
    List<StrippedSourceContainer> strippedSecrets=strippedSecrets(coreV1Api,namespace);
    if (strippedSecrets.isEmpty()) {
      return MultipleSourcesContainer.empty();
    }
    return ConfigUtils.processNamedData(strippedSecrets,environment,sourceNames,namespace,DECODE,includeDefaultProfileData);
  }
  /** 
 * <pre> 1. read all config maps in the provided namespace 2. from the above, filter the ones that we care about (by name) 3. see if any of the config maps has a single yaml/properties file 4. gather all the names of the config maps + data they hold </pre>
 */
  static MultipleSourcesContainer configMapsDataByName(  CoreV1Api coreV1Api,  String namespace,  LinkedHashSet<String> sourceNames,  Environment environment,  boolean includeDefaultProfileData){
    List<StrippedSourceContainer> strippedConfigMaps=strippedConfigMaps(coreV1Api,namespace);
    if (strippedConfigMaps.isEmpty()) {
      return MultipleSourcesContainer.empty();
    }
    return ConfigUtils.processNamedData(strippedConfigMaps,environment,sourceNames,namespace,DECODE,includeDefaultProfileData);
  }
  private static List<StrippedSourceContainer> strippedConfigMaps(  CoreV1Api coreV1Api,  String namespace){
    List<StrippedSourceContainer> strippedConfigMaps=KubernetesClientConfigMapsCache.byNamespace(coreV1Api,namespace);
    if (strippedConfigMaps.isEmpty()) {
      LOG.debug("No configmaps in namespace '" + namespace + "'");
    }
    return strippedConfigMaps;
  }
  private static List<StrippedSourceContainer> strippedSecrets(  CoreV1Api coreV1Api,  String namespace){
    List<StrippedSourceContainer> strippedSecrets=KubernetesClientSecretsCache.byNamespace(coreV1Api,namespace);
    if (strippedSecrets.isEmpty()) {
      LOG.debug("No configmaps in namespace '" + namespace + "'");
    }
    return strippedSecrets;
  }
}

