/** 
 * A cache of V1ConfigMap(s) per namespace. Makes sure we read config maps only once from a namespace.
 * @author wind57
 */
public final class KubernetesClientConfigMapsCache implements ConfigMapCache {
  private static final LogAccessor LOG=new LogAccessor(LogFactory.getLog(KubernetesClientConfigMapsCache.class));
  /** 
 * at the moment our loading of config maps is using a single thread, but might change in the future, thus a thread safe structure.
 */
  private static final ConcurrentHashMap<String,List<StrippedSourceContainer>> CACHE=new ConcurrentHashMap<>();
  @Override public void discardAll(){
    CACHE.clear();
  }
  static List<StrippedSourceContainer> byNamespace(  CoreV1Api coreV1Api,  String namespace){
    boolean[] b=new boolean[1];
    List<StrippedSourceContainer> result=CACHE.computeIfAbsent(namespace,x -> {
      try {
        b[0]=true;
        return strippedConfigMaps(coreV1Api.listNamespacedConfigMap(namespace,null,null,null,null,null,null,null,null,null,null,null).getItems());
      }
 catch (      ApiException apiException) {
        throw new RuntimeException(apiException.getResponseBody(),apiException);
      }
    }
);
    if (b[0]) {
      LOG.debug(() -> "Loaded all config maps in namespace '" + namespace + "'");
    }
 else {
      LOG.debug(() -> "Loaded (from cache) all config maps in namespace '" + namespace + "'");
    }
    return result;
  }
  private static List<StrippedSourceContainer> strippedConfigMaps(  List<V1ConfigMap> configMaps){
    return configMaps.stream().map(configMap -> new StrippedSourceContainer(configMap.getMetadata().getLabels(),configMap.getMetadata().getName(),configMap.getData())).collect(Collectors.toList());
  }
}

