        filter[0]=ConfigReloadProperties.RELOAD_LABEL_FILTER + "=true";
      }
      SharedInformerFactory factory=new SharedInformerFactory(apiClient);
      factories.add(factory);
      informer=factory.sharedIndexInformerFor((      CallGeneratorParams params) -> coreV1Api.listNamespacedSecretCall(namespace,null,null,null,null,filter[0],null,params.resourceVersion,null,null,params.timeoutSeconds,params.watch,null),V1Secret.class,V1SecretList.class);
