class KubernetesClientConfigMapPropertySourceTests {
  private static final V1ConfigMapList PROPERTIES_CONFIGMAP_LIST=new V1ConfigMapList().addItemsItem(new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("bootstrap-640").withNamespace("default").withResourceVersion("1").build()).addToData("application.properties","spring.cloud.kubernetes.configuration.watcher.refreshDelay=0\n" + "logging.level.org.springframework.cloud.kubernetes=TRACE").build());
  private static final V1ConfigMapList YAML_CONFIGMAP_LIST=new V1ConfigMapList().addItemsItem(new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("bootstrap-641").withNamespace("default").withResourceVersion("1").build()).addToData("application.yaml","dummy:\n  property:\n    string2: \"a\"\n    int2: 1\n    bool2: true\n").build());
  private static WireMockServer wireMockServer;
  @BeforeAll public static void setup(){
    wireMockServer=new WireMockServer(options().dynamicPort());
    wireMockServer.start();
    WireMock.configureFor("localhost",wireMockServer.port());
    ApiClient client=new ClientBuilder().setBasePath("http://localhost:" + wireMockServer.port()).build();
    client.setDebugging(true);
    Configuration.setDefaultApiClient(client);
  }
  @AfterAll public static void after(){
    wireMockServer.stop();
  }
  @AfterEach public void afterEach(){
    WireMock.reset();
    new KubernetesClientConfigMapsCache().discardAll();
  }
  @Test public void propertiesFile(){
    CoreV1Api api=new CoreV1Api();
    stubFor(get("/api/v1/namespaces/default/configmaps").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(PROPERTIES_CONFIGMAP_LIST))));
    NormalizedSource source=new NamedConfigMapNormalizedSource("bootstrap-640","default",false,true);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,"default",new MockEnvironment());
    KubernetesClientConfigMapPropertySource propertySource=new KubernetesClientConfigMapPropertySource(context);
    verify(getRequestedFor(urlEqualTo("/api/v1/namespaces/default/configmaps")));
    assertThat(propertySource.containsProperty("spring.cloud.kubernetes.configuration.watcher.refreshDelay")).isTrue();
    assertThat(propertySource.getProperty("spring.cloud.kubernetes.configuration.watcher.refreshDelay")).isEqualTo("0");
    assertThat(propertySource.containsProperty("logging.level.org.springframework.cloud.kubernetes")).isTrue();
    assertThat(propertySource.getProperty("logging.level.org.springframework.cloud.kubernetes")).isEqualTo("TRACE");
  }
  @Test public void yamlFile(){
    CoreV1Api api=new CoreV1Api();
    stubFor(get("/api/v1/namespaces/default/configmaps").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(YAML_CONFIGMAP_LIST))));
    NormalizedSource source=new NamedConfigMapNormalizedSource("bootstrap-641","default",false,true);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,"default",new MockEnvironment());
    KubernetesClientConfigMapPropertySource propertySource=new KubernetesClientConfigMapPropertySource(context);
    verify(getRequestedFor(urlEqualTo("/api/v1/namespaces/default/configmaps")));
    assertThat(propertySource.containsProperty("dummy.property.string2")).isTrue();
    assertThat(propertySource.getProperty("dummy.property.string2")).isEqualTo("a");
    assertThat(propertySource.containsProperty("dummy.property.int2")).isTrue();
    assertThat(propertySource.getProperty("dummy.property.int2")).isEqualTo(1);
    assertThat(propertySource.containsProperty("dummy.property.bool2")).isTrue();
    assertThat(propertySource.getProperty("dummy.property.bool2")).isEqualTo(true);
  }
  @Test public void propertiesFileWithPrefix(){
    CoreV1Api api=new CoreV1Api();
    stubFor(get("/api/v1/namespaces/default/configmaps").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(PROPERTIES_CONFIGMAP_LIST))));
    ConfigUtils.Prefix prefix=ConfigUtils.findPrefix("prefix",false,false,null);
