  @Test void constructorWithNamespaceMustNotFail(){
    ConfigUtils.Prefix prefix=ConfigUtils.findPrefix("prefix",false,false,null);
    NormalizedSource source=new NamedConfigMapNormalizedSource("bootstrap-640","default",false,prefix,true);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(new CoreV1Api(),source,"default",new MockEnvironment());
    assertThat(new KubernetesClientConfigMapPropertySource(context)).isNotNull();
  }
  @Test public void constructorShouldThrowExceptionOnFailureWhenFailFastIsEnabled(){
    stubFor(get("/api/v1/namespaces/default/configmaps").willReturn(aResponse().withStatus(500).withBody("Internal Server Error")));
    ConfigUtils.Prefix prefix=ConfigUtils.findPrefix("prefix",false,false,null);
    NormalizedSource source=new NamedConfigMapNormalizedSource("my-config","default",true,prefix,true);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(new CoreV1Api(),source,"default",new MockEnvironment());
    assertThatThrownBy(() -> new KubernetesClientConfigMapPropertySource(context)).isInstanceOf(IllegalStateException.class).hasMessage("Internal Server Error");
    verify(getRequestedFor(urlEqualTo("/api/v1/namespaces/default/configmaps")));
  }
  @Test public void constructorShouldNotThrowExceptionOnFailureWhenFailFastIsDisabled(){
    stubFor(get("/api/v1/namespaces/default/configmaps").willReturn(aResponse().withStatus(500).withBody("Internal Server Error")));
    ConfigUtils.Prefix prefix=ConfigUtils.findPrefix("prefix",false,false,null);
    NormalizedSource source=new NamedConfigMapNormalizedSource("my-config","default",false,prefix,true);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(new CoreV1Api(),source,"default",new MockEnvironment());
    assertThatNoException().isThrownBy((() -> new KubernetesClientConfigMapPropertySource(context)));
    verify(getRequestedFor(urlEqualTo("/api/v1/namespaces/default/configmaps")));
  }
}

