/** 
 * @author wind57
 */
@ExtendWith(OutputCaptureExtension.class) class NamedConfigMapContextToSourceDataProviderTests {
  private static final String NAMESPACE="default";
  private static final String RED_CONFIG_MAP_NAME="red";
  private static final String RED_WITH_PROFILE_CONFIG_MAP_NAME=RED_CONFIG_MAP_NAME + "-with-profile";
  private static final String BLUE_CONFIG_MAP_NAME="blue";
  private static final Map<String,String> COLOR_REALLY_RED=Map.of("color","really-red");
  private static final Map<String,String> TASTE_MANGO=Map.of("taste","mango");
  @BeforeAll static void setup(){
    WireMockServer wireMockServer=new WireMockServer(options().dynamicPort());
    wireMockServer.start();
    WireMock.configureFor("localhost",wireMockServer.port());
    ApiClient client=new ClientBuilder().setBasePath("http://localhost:" + wireMockServer.port()).build();
    client.setDebugging(true);
    Configuration.setDefaultApiClient(client);
  }
  @AfterEach void afterEach(){
    WireMock.reset();
    new KubernetesClientConfigMapsCache().discardAll();
  }
  /** 
 * <pre> one configmap deployed with name "red" we search by name, but for the "blue" one, as such not find it </pre>
 */
  @Test void noMatch(){
    V1ConfigMap redConfigMap=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_CONFIG_MAP_NAME).withNamespace(NAMESPACE).build()).addToData(COLOR_REALLY_RED).build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(redConfigMap);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource source=new NamedConfigMapNormalizedSource(BLUE_CONFIG_MAP_NAME,NAMESPACE,true,false);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData data=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.blue.default");
    Assertions.assertEquals(sourceData.sourceData(),Map.of());
  }
  /** 
 * <pre> one configmap deployed with name "red" we search by name, for the "red" one, as such we find it </pre>
 */
  @Test void match(){
    V1ConfigMap configMap=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_CONFIG_MAP_NAME).withNamespace(NAMESPACE).build()).addToData(COLOR_REALLY_RED).build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(configMap);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource source=new NamedConfigMapNormalizedSource(RED_CONFIG_MAP_NAME,NAMESPACE,true,false);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData data=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.red.default");
    Assertions.assertEquals(sourceData.sourceData(),COLOR_REALLY_RED);
  }
  /** 
 * <pre> - two configmaps deployed : "red" and "red-with-profile". - "red" is matched directly, "red-with-profile" is matched because we have an active profile "active-profile" </pre>
 */
  @Test void matchIncludeSingleProfile(){
    V1ConfigMap red=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_CONFIG_MAP_NAME).withNamespace(NAMESPACE).build()).addToData(COLOR_REALLY_RED).build();
    V1ConfigMap redWithProfile=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_WITH_PROFILE_CONFIG_MAP_NAME).withNamespace(NAMESPACE).build()).addToData(TASTE_MANGO).build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(red).addItemsItem(redWithProfile);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource source=new NamedConfigMapNormalizedSource(RED_CONFIG_MAP_NAME,NAMESPACE,true,ConfigUtils.Prefix.DEFAULT,true,true);
    MockEnvironment environment=new MockEnvironment();
    environment.setActiveProfiles("with-profile");
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,environment,false);
    KubernetesClientContextToSourceData data=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.red.red-with-profile.default.with-profile");
    Assertions.assertEquals(sourceData.sourceData().size(),1);
    Assertions.assertEquals(sourceData.sourceData().get("taste"),"mango");
  }
  /** 
 * <pre> - two configmaps deployed : "red" and "red-with-profile". - "red" is matched directly, "red-with-profile" is matched because we have an active profile "active-profile" -  This takes into consideration the prefix, that we explicitly specify. Notice that prefix works for profile based config maps as well. </pre>
 */
  @Test void matchIncludeSingleProfileWithPrefix(){
    V1ConfigMap red=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_CONFIG_MAP_NAME).withNamespace(NAMESPACE).build()).addToData(COLOR_REALLY_RED).build();
    V1ConfigMap redWithTaste=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_WITH_PROFILE_CONFIG_MAP_NAME).withNamespace(NAMESPACE).build()).addToData(TASTE_MANGO).build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(red).addItemsItem(redWithTaste);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    ConfigUtils.Prefix prefix=ConfigUtils.findPrefix("some",false,false,null);
    NormalizedSource source=new NamedConfigMapNormalizedSource(RED_CONFIG_MAP_NAME,NAMESPACE,true,prefix,true);
    MockEnvironment environment=new MockEnvironment();
    environment.setActiveProfiles("with-profile");
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,environment);
    KubernetesClientContextToSourceData data=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.red.red-with-profile.default");
    Assertions.assertEquals(sourceData.sourceData().size(),2);
    Assertions.assertEquals(sourceData.sourceData().get("some.color"),"really-red");
    Assertions.assertEquals(sourceData.sourceData().get("some.taste"),"mango");
  }
  /** 
 * <pre> - three configmaps deployed : "red", "red-with-taste" and "red-with-shape" - "red" is matched directly, the other two are matched because of active profiles -  This takes into consideration the prefix, that we explicitly specify. Notice that prefix works for profile based config maps as well. </pre>
 */
  @Test void matchIncludeTwoProfilesWithPrefix(){
    V1ConfigMap red=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_CONFIG_MAP_NAME).withNamespace(NAMESPACE).build()).addToData(COLOR_REALLY_RED).build();
    V1ConfigMap redWithTaste=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_CONFIG_MAP_NAME + "-with-taste").withNamespace(NAMESPACE).withResourceVersion("1").build()).addToData(TASTE_MANGO).build();
    V1ConfigMap redWithShape=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_CONFIG_MAP_NAME + "-with-shape").withNamespace(NAMESPACE).build()).addToData("shape","round").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(red).addItemsItem(redWithTaste).addItemsItem(redWithShape);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    ConfigUtils.Prefix prefix=ConfigUtils.findPrefix("some",false,false,null);
    NormalizedSource source=new NamedConfigMapNormalizedSource(RED_CONFIG_MAP_NAME,NAMESPACE,true,prefix,true);
    MockEnvironment environment=new MockEnvironment();
    environment.setActiveProfiles("with-taste","with-shape");
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,environment);
    KubernetesClientContextToSourceData data=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.red.red-with-shape.red-with-taste.default");
    Assertions.assertEquals(sourceData.sourceData().size(),3);
    Assertions.assertEquals(sourceData.sourceData().get("some.color"),"really-red");
    Assertions.assertEquals(sourceData.sourceData().get("some.taste"),"mango");
    Assertions.assertEquals(sourceData.sourceData().get("some.shape"),"round");
  }
  /** 
 * <pre> proves that an implicit configmap is going to be generated and read, even if we did not provide one </pre>
 */
  @Test void matchWithName(){
    V1ConfigMap red=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("application").withNamespace(NAMESPACE).build()).addToData("color","red").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(red);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    ConfigUtils.Prefix prefix=ConfigUtils.findPrefix("some",false,false,null);
    NormalizedSource source=new NamedConfigMapNormalizedSource("application",NAMESPACE,true,prefix,false);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData data=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.application.default");
    Assertions.assertEquals(sourceData.sourceData(),Collections.singletonMap("some.color","red"));
  }
  /** 
 * <pre> - NamedSecretContextToSourceDataProvider gets as input a KubernetesClientConfigContext. - This context has a namespace as well as a NormalizedSource, that has a namespace too. - This test makes sure that we use the proper one. </pre>
 */
  @Test void namespaceMatch(){
    V1ConfigMap configMap=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_CONFIG_MAP_NAME).withNamespace(NAMESPACE).build()).addToData(COLOR_REALLY_RED).build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(configMap);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    String wrongNamespace=NAMESPACE + "nope";
    NormalizedSource source=new NamedConfigMapNormalizedSource(RED_CONFIG_MAP_NAME,wrongNamespace,true,false);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData data=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.red.default");
    Assertions.assertEquals(sourceData.sourceData(),COLOR_REALLY_RED);
  }
  /** 
 * <pre> - proves that single yaml file gets special treatment </pre>
 */
  @Test void testSingleYaml(){
    V1ConfigMap singleYaml=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName(RED_CONFIG_MAP_NAME).withNamespace(NAMESPACE).build()).addToData("single.yaml","key: value").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(singleYaml);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource source=new NamedConfigMapNormalizedSource(RED_CONFIG_MAP_NAME,NAMESPACE,true,false);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData data=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.red.default");
    Assertions.assertEquals(sourceData.sourceData(),Map.of("key","value"));
  }
  /** 
 * <pre> - one configmap is deployed with name "one" - profile is enabled with name "k8s" we assert that the name of the source is "one" and does not contain "one-dev" </pre>
 */
  @Test void testCorrectNameWithProfile(){
    V1ConfigMap one=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("one").withNamespace(NAMESPACE).build()).addToData("key","value").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(one);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    MockEnvironment environment=new MockEnvironment();
    environment.setActiveProfiles("k8s");
    NormalizedSource source=new NamedConfigMapNormalizedSource("one",NAMESPACE,true,true);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,environment);
    KubernetesClientContextToSourceData data=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.one.default");
    Assertions.assertEquals(sourceData.sourceData(),Map.of("key","value"));
  }
  /** 
 * <pre> - one configmap is deployed with name "red" - one configmap is deployed with name "green" - we first search for "red" and find it, and it is retrieved from the cluster via the client. - we then search for the "green" one, and it is retrieved from the cache this time. </pre>
 */
  @Test void cache(  CapturedOutput output){
    V1ConfigMap red=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("red").withNamespace(NAMESPACE).build()).addToData("color","red").build();
    V1ConfigMap green=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("green").withNamespace(NAMESPACE).build()).addToData("color","green").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(red).addItemsItem(green);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    MockEnvironment environment=new MockEnvironment();
    NormalizedSource redSource=new NamedConfigMapNormalizedSource("red",NAMESPACE,true,false);
    KubernetesClientConfigContext redContext=new KubernetesClientConfigContext(api,redSource,NAMESPACE,environment);
    KubernetesClientContextToSourceData redData=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData redSourceData=redData.apply(redContext);
    Assertions.assertEquals(redSourceData.sourceName(),"configmap.red.default");
    Assertions.assertEquals(redSourceData.sourceData(),Map.of("color","red"));
    Assertions.assertTrue(output.getAll().contains("Loaded all config maps in namespace '" + NAMESPACE + "'"));
    NormalizedSource greenSource=new NamedConfigMapNormalizedSource("green",NAMESPACE,true,true);
    KubernetesClientConfigContext greenContext=new KubernetesClientConfigContext(api,greenSource,NAMESPACE,environment);
    KubernetesClientContextToSourceData greenData=new NamedConfigMapContextToSourceDataProvider().get();
    SourceData greenSourceData=greenData.apply(greenContext);
    Assertions.assertEquals(greenSourceData.sourceName(),"configmap.green.default");
    Assertions.assertEquals(greenSourceData.sourceData(),Map.of("color","green"));
    String[] out=output.getAll().split("Loaded all config maps in namespace");
    Assertions.assertEquals(out.length,2);
    out=output.getAll().split("Loaded \\(from cache\\) all config maps in namespace");
    Assertions.assertEquals(out.length,2);
  }
  private void stubCall(  V1ConfigMapList list){
    stubFor(get("/api/v1/namespaces/default/configmaps").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(list))));
  }
}

