public class ConfigMapEnableRetryWithoutFailFastTest {
  private static final String API="/api/v1/namespaces/default/configmaps";
  private static final String SECRETS_API="/api/v1/namespaces/default/secrets";
  private ConfigurableApplicationContext context;
  private static WireMockServer wireMockServer;
  private static MockedStatic<KubernetesClientUtils> clientUtilsMock;
  @BeforeAll public static void setup(){
    wireMockServer=new WireMockServer(options().dynamicPort());
    wireMockServer.start();
    WireMock.configureFor(wireMockServer.port());
    clientUtilsMock=mockStatic(KubernetesClientUtils.class);
    clientUtilsMock.when(KubernetesClientUtils::kubernetesApiClient).thenReturn(new ClientBuilder().setBasePath(wireMockServer.baseUrl()).build());
    stubConfigMapAndSecretsDefaults();
  }
  private static void stubConfigMapAndSecretsDefaults(){
    stubFor(get(API).willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(new V1ConfigMapList()))));
    stubFor(get(SECRETS_API).willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(new V1SecretList()))));
  }
  @AfterAll public static void teardown(){
    wireMockServer.stop();
    clientUtilsMock.close();
  }
  protected void setup(  String... env){
    List<String> envList=(env != null) ? new ArrayList<>(Arrays.asList(env)) : new ArrayList<>();
    envList.add("spring.cloud.kubernetes.client.namespace=default");
    String[] envArray=envList.toArray(new String[0]);
    context=new SpringApplicationBuilder(RetryConfiguration.class,PropertyPlaceholderAutoConfiguration.class,ConfigReloadAutoConfiguration.class,RefreshAutoConfiguration.class,EndpointAutoConfiguration.class,InfoEndpointAutoConfiguration.class,RefreshEndpointAutoConfiguration.class,ConfigurationPropertiesBindingPostProcessor.class,ConfigurationPropertiesRebinderAutoConfiguration.class,KubernetesClientBootstrapConfiguration.class,KubernetesClientRetryBootstrapConfiguration.class,KubernetesBootstrapConfiguration.class,KubernetesClientConfigReloadAutoConfiguration.class).web(org.springframework.boot.WebApplicationType.NONE).properties(envArray).run();
  }
  @AfterEach public void afterEach(){
    if (this.context != null) {
      this.context.close();
      this.context=null;
    }
  }
  @Test public void doesNotContainRetryableConfigMapPropertySourceLocator() throws Exception {
    stubFor(get(API).willReturn(aResponse().withStatus(500).withBody("Internal Server Error")));
    setup("debug=true","spring.main.cloud-platform=KUBERNETES","spring.cloud.kubernetes.test.enable-retry=true");
    assertThat(context.containsBean("retryableConfigMapPropertySourceLocator")).isFalse();
  }
}

