/** 
 * @author Isik Erhan
 */
abstract class ConfigRetryEnabled {
  private static final String API="/api/v1/namespaces/default/configmaps";
  private static WireMockServer wireMockServer;
  private static MockedStatic<KubernetesClientUtils> clientUtilsMock;
  @BeforeAll static void setup(){
    wireMockServer=new WireMockServer(options().dynamicPort());
    wireMockServer.start();
    WireMock.configureFor(wireMockServer.port());
    clientUtilsMock=mockStatic(KubernetesClientUtils.class);
    clientUtilsMock.when(KubernetesClientUtils::kubernetesApiClient).thenReturn(new ClientBuilder().setBasePath(wireMockServer.baseUrl()).build());
    clientUtilsMock.when(() -> KubernetesClientUtils.getApplicationNamespace(Mockito.any(),Mockito.any(),Mockito.any())).thenReturn("default");
    stubConfigMapAndSecretsDefaults();
  }
  private static void stubConfigMapAndSecretsDefaults(){
    stubFor(get(API).willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(new V1ConfigMapList()))));
  }
  @AfterAll static void teardown(){
    wireMockServer.stop();
    clientUtilsMock.close();
  }
  @AfterEach void afterEach(){
    WireMock.reset();
    stubConfigMapAndSecretsDefaults();
  }
  @Autowired private ConfigMapPropertySourceLocator retryablePl;
  @Test void locateShouldNotRetryWhenThereIsNoFailure(){
    ConfigMapPropertySourceLocator propertySourceLocator=spy(retryablePl);
    Map<String,String> data=new HashMap<>();
    data.put("some.prop","theValue");
    data.put("some.number","0");
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(new V1ConfigMap().metadata(new V1ObjectMeta().name("application")).data(data));
    stubFor(get(API).willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(configMapList))));
    PropertySource<?> propertySource=Assertions.assertDoesNotThrow(() -> propertySourceLocator.locate(new MockEnvironment()));
    WireMock.verify(1,getRequestedFor(urlEqualTo(API)));
    assertThat(propertySource.getProperty("some.prop")).isEqualTo("theValue");
    assertThat(propertySource.getProperty("some.number")).isEqualTo("0");
  }
  @Test void locateShouldRetryAndRecover(){
    ConfigMapPropertySourceLocator propertySourceLocator=spy(retryablePl);
    Map<String,String> data=new HashMap<>();
    data.put("some.prop","theValue");
    data.put("some.number","0");
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(new V1ConfigMap().metadata(new V1ObjectMeta().name("application")).data(data));
    stubFor(get(API).inScenario("Retry and Recover").whenScenarioStateIs(STARTED).willReturn(aResponse().withStatus(500)).willSetStateTo("Failed once"));
    stubFor(get(API).inScenario("Retry and Recover").whenScenarioStateIs("Failed once").willReturn(aResponse().withStatus(500)).willSetStateTo("Failed twice"));
    stubFor(get(API).inScenario("Retry and Recover").whenScenarioStateIs("Failed twice").willReturn(aResponse().withStatus(500)).willSetStateTo("Failed thrice"));
    stubFor(get(API).inScenario("Retry and Recover").whenScenarioStateIs("Failed thrice").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(configMapList))));
    PropertySource<?> propertySource=Assertions.assertDoesNotThrow(() -> propertySourceLocator.locate(new MockEnvironment()));
    WireMock.verify(5,getRequestedFor(urlEqualTo(API)));
    assertThat(propertySource.getProperty("some.prop")).isEqualTo("theValue");
    assertThat(propertySource.getProperty("some.number")).isEqualTo("0");
  }
  @Test void locateShouldRetryAndFail(){
    ConfigMapPropertySourceLocator propertySourceLocator=spy(retryablePl);
    stubFor(get(API).willReturn(aResponse().withStatus(500).withBody("Internal Server Error")));
    assertThatThrownBy(() -> propertySourceLocator.locate(new MockEnvironment())).isInstanceOf(IllegalStateException.class).hasMessage("Internal Server Error");
    WireMock.verify(5,getRequestedFor(urlEqualTo(API)));
  }
}

