/** 
 * @author wind57
 */
@ExtendWith(OutputCaptureExtension.class) class LabeledConfigMapContextToSourceDataProviderTests {
  private static final Map<String,String> LABELS=new LinkedHashMap<>();
  private static final Map<String,String> RED_LABEL=Map.of("color","red");
  private static final Map<String,String> BLUE_LABEL=Map.of("color","blue");
  private static final Map<String,String> PINK_LABEL=Map.of("color","pink");
  private static final String NAMESPACE="default";
static {
    LABELS.put("label2","value2");
    LABELS.put("label1","value1");
  }
  @BeforeAll static void setup(){
    WireMockServer wireMockServer=new WireMockServer(options().dynamicPort());
    wireMockServer.start();
    WireMock.configureFor("localhost",wireMockServer.port());
    ApiClient client=new ClientBuilder().setBasePath("http://localhost:" + wireMockServer.port()).build();
    client.setDebugging(true);
    Configuration.setDefaultApiClient(client);
  }
  @AfterEach void afterEach(){
    WireMock.reset();
    new KubernetesClientConfigMapsCache().discardAll();
  }
  /** 
 * we have a single config map deployed. it has two labels and these match against our queries.
 */
  @Test void singleConfigMapMatchAgainstLabels(){
    V1ConfigMap one=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("test-configmap").withLabels(LABELS).withNamespace(NAMESPACE).build()).addToData("name","value").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(one);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource source=new LabeledConfigMapNormalizedSource(NAMESPACE,LABELS,true,false);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData data=new LabeledConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals("configmap.test-configmap.default",sourceData.sourceName());
    Assertions.assertEquals(Map.of("name","value"),sourceData.sourceData());
  }
  /** 
 * we have three configmaps deployed. two of them have labels that match (color=red), one does not (color=blue).
 */
  @Test void twoConfigMapsMatchAgainstLabels(){
    V1ConfigMap redOne=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("red-configmap").withLabels(RED_LABEL).withNamespace(NAMESPACE).build()).addToData("colorOne","really-red").build();
    V1ConfigMap redTwo=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("red-configmap-again").withLabels(RED_LABEL).withNamespace(NAMESPACE).build()).addToData("colorTwo","really-red-again").build();
    V1ConfigMap blue=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("blue-configmap").withLabels(BLUE_LABEL).withNamespace(NAMESPACE).build()).addToData("color","blue").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(redOne).addItemsItem(redTwo).addItemsItem(blue);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource source=new LabeledConfigMapNormalizedSource(NAMESPACE,RED_LABEL,true,false);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData data=new LabeledConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.red-configmap.red-configmap-again.default");
    Assertions.assertEquals(sourceData.sourceData().size(),2);
    Assertions.assertEquals(sourceData.sourceData().get("colorOne"),"really-red");
    Assertions.assertEquals(sourceData.sourceData().get("colorTwo"),"really-red-again");
  }
  /** 
 * one configmap deployed (pink), does not match our query (blue).
 */
  @Test void configMapNoMatch(){
    V1ConfigMap one=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("pink-configmap").withLabels(PINK_LABEL).withNamespace(NAMESPACE).build()).addToData("color","pink").build();
