 */
  @Test void testTwoConfigmapsWithPrefix(){
    V1ConfigMap one=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("blue-configmap").withLabels(BLUE_LABEL).withNamespace(NAMESPACE).build()).addToData("first","blue").build();
    V1ConfigMap two=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("another-blue-configmap").withLabels(BLUE_LABEL).withNamespace(NAMESPACE).build()).addToData("second","blue").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(one).addItemsItem(two);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource source=new LabeledConfigMapNormalizedSource(NAMESPACE,BLUE_LABEL,true,ConfigUtils.Prefix.DELAYED,false);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData data=new LabeledConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceName(),"configmap.another-blue-configmap.blue-configmap.default");
    Map<String,Object> properties=sourceData.sourceData();
    Assertions.assertEquals(2,properties.size());
    Iterator<String> keys=properties.keySet().iterator();
    String firstKey=keys.next();
    String secondKey=keys.next();
    if (firstKey.contains("first")) {
      Assertions.assertEquals(firstKey,"another-blue-configmap.blue-configmap.first");
    }
    Assertions.assertEquals(secondKey,"another-blue-configmap.blue-configmap.second");
    Assertions.assertEquals(properties.get(firstKey),"blue");
    Assertions.assertEquals(properties.get(secondKey),"blue");
  }
  /** 
 * two configmaps are deployed: "color-configmap" with label: "{color:blue}" and "color-configmap-k8s" with no labels. We search by "{color:red}", do not find anything and thus have an empty SourceData. profile based sources are enabled, but it has no effect.
 */
  @Test void searchWithLabelsNoConfigmapsFound(){
    V1ConfigMap one=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("color-configmap").withLabels(BLUE_LABEL).withNamespace(NAMESPACE).build()).addToData("one","1").build();
    V1ConfigMap two=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("color-config-k8s").withNamespace(NAMESPACE).build()).addToData("two","2").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(one).addItemsItem(two);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource source=new LabeledConfigMapNormalizedSource(NAMESPACE,RED_LABEL,true,ConfigUtils.Prefix.DEFAULT,true);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData data=new LabeledConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertTrue(sourceData.sourceData().isEmpty());
    Assertions.assertEquals(sourceData.sourceName(),"configmap.color.default");
  }
  /** 
 * two configmaps are deployed: "color-configmap" with label: "{color:blue}" and "shape-configmap" with label: "{shape:round}". We search by "{color:blue}" and find one configmap. profile based sources are enabled, but it has no effect.
 */
  @Test void searchWithLabelsOneConfigMapFound(){
    V1ConfigMap one=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("color-configmap").withLabels(BLUE_LABEL).withNamespace(NAMESPACE).build()).addToData("one","1").build();
    V1ConfigMap two=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("shape-configmap").withNamespace(NAMESPACE).build()).addToData("two","2").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(one).addItemsItem(two);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource source=new LabeledConfigMapNormalizedSource(NAMESPACE,BLUE_LABEL,true,ConfigUtils.Prefix.DEFAULT,true);
