    KubernetesClientContextToSourceData data=new LabeledConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceData().size(),1);
    Assertions.assertEquals(sourceData.sourceData().get("one"),"1");
    Assertions.assertEquals(sourceData.sourceName(),"configmap.color-configmap.default");
  }
  /** 
 * two configmaps are deployed: "color-configmap" with label: "{color:blue}" and "color-configmap-k8s" with label: "{color:red}". We search by "{color:blue}" and find one configmap. Since profiles are enabled, we will also be reading "color-configmap-k8s", even if its labels do not match provided ones.
 */
  @Test void searchWithLabelsOneConfigMapFoundAndOneFromProfileFound(){
    V1ConfigMap one=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("color-configmap").withLabels(BLUE_LABEL).withNamespace(NAMESPACE).build()).addToData("one","1").build();
    V1ConfigMap two=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("color-configmap-k8s").withLabels(RED_LABEL).withNamespace(NAMESPACE).build()).addToData("two","2").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(one).addItemsItem(two);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    MockEnvironment environment=new MockEnvironment();
    environment.setActiveProfiles("k8s");
    NormalizedSource source=new LabeledConfigMapNormalizedSource(NAMESPACE,BLUE_LABEL,true,ConfigUtils.Prefix.DELAYED,true);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,environment);
    KubernetesClientContextToSourceData data=new LabeledConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceData().size(),2);
    Assertions.assertEquals(sourceData.sourceData().get("color-configmap.color-configmap-k8s.one"),"1");
    Assertions.assertEquals(sourceData.sourceData().get("color-configmap.color-configmap-k8s.two"),"2");
    Assertions.assertEquals(sourceData.sourceName(),"configmap.color-configmap.color-configmap-k8s.default");
  }
  /** 
 * <pre> - configmap "color-configmap" with label "{color:blue}" - configmap "shape-configmap" with labels "{color:blue, shape:round}" - configmap "no-fit" with labels "{tag:no-fit}" - configmap "color-configmap-k8s" with label "{color:red}" - configmap "shape-configmap-k8s" with label "{shape:triangle}" </pre>
 */
  @Test void searchWithLabelsTwoConfigMapsFoundAndOneFromProfileFound(){
    V1ConfigMap colorConfigMap=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("color-configmap").withLabels(BLUE_LABEL).withNamespace(NAMESPACE).build()).addToData("one","1").build();
    V1ConfigMap shapeConfigmap=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("shape-configmap").withLabels(Map.of("color","blue","shape","round")).withNamespace(NAMESPACE).build()).addToData("two","2").build();
    V1ConfigMap noFit=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("no-fit").withLabels(Map.of("tag","no-fit")).withNamespace(NAMESPACE).build()).addToData("three","3").build();
    V1ConfigMap colorConfigmapK8s=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("color-configmap-k8s").withLabels(RED_LABEL).withNamespace(NAMESPACE).build()).addToData("four","4").build();
    V1ConfigMap shapeConfigmapK8s=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("shape-configmap-k8s").withLabels(Map.of("shape","triangle")).withNamespace(NAMESPACE).build()).addToData("five","5").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(colorConfigMap).addItemsItem(shapeConfigmap).addItemsItem(noFit).addItemsItem(colorConfigmapK8s).addItemsItem(shapeConfigmapK8s);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    MockEnvironment environment=new MockEnvironment();
    environment.setActiveProfiles("k8s");
    NormalizedSource source=new LabeledConfigMapNormalizedSource(NAMESPACE,BLUE_LABEL,true,ConfigUtils.Prefix.DELAYED,true);
    KubernetesClientConfigContext context=new KubernetesClientConfigContext(api,source,NAMESPACE,environment);
    KubernetesClientContextToSourceData data=new LabeledConfigMapContextToSourceDataProvider().get();
    SourceData sourceData=data.apply(context);
    Assertions.assertEquals(sourceData.sourceData().size(),4);
    Assertions.assertEquals(sourceData.sourceData().get("color-configmap.color-configmap-k8s.shape-configmap.shape-configmap-k8s.one"),"1");
    Assertions.assertEquals(sourceData.sourceData().get("color-configmap.color-configmap-k8s.shape-configmap.shape-configmap-k8s.two"),"2");
    Assertions.assertEquals(sourceData.sourceData().get("color-configmap.color-configmap-k8s.shape-configmap.shape-configmap-k8s.four"),"4");
    Assertions.assertEquals(sourceData.sourceData().get("color-configmap.color-configmap-k8s.shape-configmap.shape-configmap-k8s.five"),"5");
    Assertions.assertEquals(sourceData.sourceName(),"configmap.color-configmap.color-configmap-k8s.shape-configmap.shape-configmap-k8s.default");
  }
  /** 
 * <pre> - one configmap is deployed with label {"color", "red"} - one configmap is deployed with label {"color", "green"} - we first search for "red" and find it, and it is retrieved from the cluster via the client. - we then search for the "green" one, and it is retrieved from the cache this time. </pre>
 */
  @Test void cache(  CapturedOutput output){
    V1ConfigMap red=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withLabels(Map.of("color","red")).withNamespace(NAMESPACE).withName("red-configmap").build()).addToData("color","red").build();
    V1ConfigMap green=new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withLabels(Map.of("color","green")).withNamespace(NAMESPACE).withName("green-configmap").build()).addToData("color","green").build();
    V1ConfigMapList configMapList=new V1ConfigMapList().addItemsItem(red).addItemsItem(green);
    stubCall(configMapList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource redSource=new LabeledConfigMapNormalizedSource(NAMESPACE,Map.of("color","red"),false,ConfigUtils.Prefix.DEFAULT,false);
    KubernetesClientConfigContext redContext=new KubernetesClientConfigContext(api,redSource,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData redData=new LabeledConfigMapContextToSourceDataProvider().get();
    SourceData redSourceData=redData.apply(redContext);
    Assertions.assertEquals(redSourceData.sourceData().size(),1);
    Assertions.assertEquals(redSourceData.sourceData().get("color"),"red");
    Assertions.assertEquals(redSourceData.sourceName(),"configmap.red-configmap.default");
    Assertions.assertTrue(output.getAll().contains("Loaded all config maps in namespace '" + NAMESPACE + "'"));
    NormalizedSource greenSource=new LabeledConfigMapNormalizedSource(NAMESPACE,Map.of("color","green"),false,ConfigUtils.Prefix.DEFAULT,false);
    KubernetesClientConfigContext greenContext=new KubernetesClientConfigContext(api,greenSource,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData greenData=new LabeledConfigMapContextToSourceDataProvider().get();
    SourceData greenSourceData=greenData.apply(greenContext);
    Assertions.assertEquals(greenSourceData.sourceData().size(),1);
    Assertions.assertEquals(greenSourceData.sourceData().get("color"),"green");
    Assertions.assertEquals(greenSourceData.sourceName(),"configmap.green-configmap.default");
    String[] out=output.getAll().split("Loaded all config maps in namespace");
    Assertions.assertEquals(out.length,2);
    out=output.getAll().split("Loaded \\(from cache\\) all config maps in namespace");
    Assertions.assertEquals(out.length,2);
  }
  private void stubCall(  V1ConfigMapList list){
    stubFor(get("/api/v1/namespaces/default/configmaps").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(list))));
  }
}

