public class KubernetesClientConfigReloadAutoConfigurationTest {
  private ConfigurableApplicationContext context;
  public static WireMockServer wireMockServer;
  protected void setup(  String... env){
    List<String> envList=(env != null) ? new ArrayList<>(Arrays.asList(env)) : new ArrayList<>();
    envList.add("spring.cloud.kubernetes.client.namespace=default");
    String[] envArray=envList.toArray(new String[0]);
    context=new SpringApplicationBuilder(PropertyPlaceholderAutoConfiguration.class,LocalTestConfig.class,ConfigReloadAutoConfiguration.class,RefreshAutoConfiguration.class,EndpointAutoConfiguration.class,InfoEndpointAutoConfiguration.class,RefreshEndpointAutoConfiguration.class,ConfigurationPropertiesBindingPostProcessor.class,ConfigReloadPropertiesAutoConfiguration.class,ConfigurationPropertiesRebinderAutoConfiguration.class,KubernetesClientBootstrapConfiguration.class,KubernetesBootstrapConfiguration.class,KubernetesClientConfigReloadAutoConfiguration.class).web(org.springframework.boot.WebApplicationType.NONE).properties(envArray).run();
  }
  @BeforeAll static void startWireMockServer(){
    wireMockServer=new WireMockServer(options().dynamicPort());
    wireMockServer.start();
    WireMock.configureFor(wireMockServer.port());
  }
  @AfterEach void afterEach(){
    if (this.context != null) {
      this.context.close();
      this.context=null;
    }
  }
  @BeforeEach void beforeEach(){
    V1ConfigMapList TEST_CONFIGMAP=new V1ConfigMapList().addItemsItem(new V1ConfigMapBuilder().withMetadata(new V1ObjectMetaBuilder().withName("test-cm").withNamespace("default").withResourceVersion("1").build()).addToData("app.name","test").build());
    WireMock.stubFor(get(urlMatching("^/api/v1/namespaces/default/configmaps.*")).willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(TEST_CONFIGMAP))));
  }
  @Test void kubernetesWatchersWhenKubernetesDisabled(){
    setup();
    assertThat(context.containsBean("configMapPropertySourceLocator")).isFalse();
    assertThat(context.containsBean("secretsPropertySourceLocator")).isFalse();
    assertThat(context.containsBean("configMapPropertyChangePollingWatcher")).isFalse();
    assertThat(context.containsBean("secretsPropertyChangePollingWatcher")).isFalse();
    assertThat(context.containsBean("configMapPropertyChangeEventWatcher")).isFalse();
    assertThat(context.containsBean("secretsPropertyChangeEventWatcher")).isFalse();
  }
  @Test void kubernetesWatchersWhenConfigDisabled(){
    setup("spring.cloud.kubernetes.config.enabled=false");
    assertThat(context.containsBean("configMapPropertyChangePollingWatcher")).isFalse();
    assertThat(context.containsBean("secretsPropertyChangePollingWatcher")).isFalse();
    assertThat(context.containsBean("configMapPropertyChangeEventWatcher")).isFalse();
    assertThat(context.containsBean("secretsPropertyChangeEventWatcher")).isFalse();
  }
  @Test void kubernetesWatchersWhenReloadDisabled(){
    setup("spring.cloud.kubernetes.reload.enabled=false");
    assertThat(context.containsBean("configMapPropertyChangePollingWatcher")).isFalse();
    assertThat(context.containsBean("secretsPropertyChangePollingWatcher")).isFalse();
    assertThat(context.containsBean("configMapPropertyChangeEventWatcher")).isFalse();
    assertThat(context.containsBean("secretsPropertyChangeEventWatcher")).isFalse();
  }
  @Test void kubernetesReloadEnabledButSecretAndConfigDisabled(){
    setup("spring.cloud.kubernetes.reload.enabled=true","spring.cloud.kubernetes.config.enabled=false","spring.cloud.kubernetes.secrets.enabled=false");
    assertThat(context.containsBean("configMapPropertyChangePollingWatcher")).isFalse();
    assertThat(context.containsBean("secretsPropertyChangePollingWatcher")).isFalse();
    assertThat(context.containsBean("configMapPropertyChangeEventWatcher")).isFalse();
    assertThat(context.containsBean("secretsPropertyChangeEventWatcher")).isFalse();
  }
  /** 
 * <pre> - reload mode is enabled (via event reload) - spring.cloud.kubernetes.reload.monitoring-configMaps is true by default - config map event watcher is picked up - config map polling watcher is not picked up - secrets event watcher is not picked up - secrets polling watcher is not picked up </pre>
 */
  @Test void reloadEventEnabledMonitoringConfigMapsEnabledByDefault(){
    setup("spring.cloud.kubernetes.reload.enabled=true","spring.main.cloud-platform=KUBERNETES");
    Map<String,ConfigurationChangeDetector> map=context.getBeansOfType(ConfigurationChangeDetector.class);
    Assertions.assertEquals(map.size(),1);
    Assertions.assertTrue(map.values().iterator().next().getClass().isAssignableFrom(KubernetesClientEventBasedConfigMapChangeDetector.class));
  }
  /** 
 * <pre> - reload mode is enabled (via event reload) - spring.cloud.kubernetes.reload.monitoring-configMaps is true by default - config map event watcher is picked up - config map polling watcher is not picked up - secrets event watcher is not picked up - secrets polling watcher is not picked up </pre>
 */
  @Test void reloadEventEnabledMonitoringConfigMapsEnabled(){
    setup("spring.cloud.kubernetes.reload.enabled=true","spring.cloud.kubernetes.reload.mode=event","spring.main.cloud-platform=KUBERNETES");
    Map<String,ConfigurationChangeDetector> map=context.getBeansOfType(ConfigurationChangeDetector.class);
    Assertions.assertEquals(map.size(),1);
    Assertions.assertTrue(map.values().iterator().next().getClass().isAssignableFrom(KubernetesClientEventBasedConfigMapChangeDetector.class));
  }
  /** 
 * <pre> - reload mode is enabled (via event reload) - spring.cloud.kubernetes.reload.monitoring-configMaps is false - config map event watcher is not picked up - config map polling watcher is not picked up - secrets event watcher is not picked up - secrets polling watcher is not picked up </pre>
 */
  @Test void reloadEventEnabledMonitoringConfigMapsDisabled(){
    setup("spring.cloud.kubernetes.reload.enabled=true","spring.cloud.kubernetes.reload.mode=event","spring.main.cloud-platform=KUBERNETES","spring.cloud.kubernetes.reload.monitoring-configMaps=false");
    Map<String,ConfigurationChangeDetector> map=context.getBeansOfType(ConfigurationChangeDetector.class);
    Assertions.assertEquals(map.size(),0);
  }
