@ExtendWith(OutputCaptureExtension.class) class LabeledSecretContextToSourceDataProviderTests {
  private static final Map<String,String> LABELS=new LinkedHashMap<>();
  private static final Map<String,String> RED_LABEL=Map.of("color","red");
  private static final String NAMESPACE="default";
static {
    LABELS.put("label2","value2");
    LABELS.put("label1","value1");
  }
  @BeforeAll static void setup(){
    WireMockServer wireMockServer=new WireMockServer(options().dynamicPort());
    wireMockServer.start();
    WireMock.configureFor("localhost",wireMockServer.port());
    ApiClient client=new ClientBuilder().setBasePath("http://localhost:" + wireMockServer.port()).build();
    client.setDebugging(true);
    Configuration.setDefaultApiClient(client);
  }
  @AfterEach void afterEach(){
    WireMock.reset();
    new KubernetesClientSecretsCache().discardAll();
  }
  /** 
 * we have a single secret deployed. it does not match our query.
 */
  @Test void noMatch(){
    V1Secret red=new V1SecretBuilder().withMetadata(new V1ObjectMetaBuilder().withLabels(Collections.singletonMap("color","red")).withNamespace(NAMESPACE).withName("red-secret").build()).addToData("color",Base64.getEncoder().encode("really-red".getBytes())).build();
