  @Test void testTwoSecretsWithPrefix(){
    V1Secret one=new V1SecretBuilder().withMetadata(new V1ObjectMetaBuilder().withLabels(Map.of("color","blue")).withNamespace(NAMESPACE).withName("blue-secret").build()).addToData("first","blue".getBytes()).build();
