 */
  @Test void testYaml(){
    V1Secret colorSecret=new V1SecretBuilder().withMetadata(new V1ObjectMetaBuilder().withLabels(Map.of("color","blue")).withNamespace(NAMESPACE).withName("color-secret").build()).addToData("test.yaml","color: blue".getBytes()).build();
