  /** 
 * <pre> - one secret is deployed with label {"color", "red"} - one secret is deployed with label {"color", "green"} - we first search for "red" and find it, and it is retrieved from the cluster via the client. - we then search for the "green" one, and it is retrieved from the cache this time. </pre>
 */
  @Test void cache(  CapturedOutput output){
    V1Secret red=new V1SecretBuilder().withMetadata(new V1ObjectMetaBuilder().withLabels(Map.of("color","red")).withNamespace(NAMESPACE).withName("red").build()).addToData("color","red".getBytes()).build();
    V1Secret green=new V1SecretBuilder().withMetadata(new V1ObjectMetaBuilder().withLabels(Map.of("color","green")).withNamespace(NAMESPACE).withName("green").build()).addToData("color","green".getBytes()).build();
    V1SecretList secretList=new V1SecretList().addItemsItem(red).addItemsItem(green);
    stubCall(secretList);
    CoreV1Api api=new CoreV1Api();
    NormalizedSource redSource=new LabeledSecretNormalizedSource(NAMESPACE,Map.of("color","red"),false,ConfigUtils.Prefix.DEFAULT,false);
    KubernetesClientConfigContext redContext=new KubernetesClientConfigContext(api,redSource,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData redData=new LabeledSecretContextToSourceDataProvider().get();
    SourceData redSourceData=redData.apply(redContext);
    Assertions.assertEquals(redSourceData.sourceData().size(),1);
    Assertions.assertEquals(redSourceData.sourceData().get("color"),"red");
    Assertions.assertEquals(redSourceData.sourceName(),"secret.red.default");
    Assertions.assertTrue(output.getAll().contains("Loaded all secrets in namespace '" + NAMESPACE + "'"));
    NormalizedSource greenSource=new LabeledSecretNormalizedSource(NAMESPACE,Map.of("color","green"),false,ConfigUtils.Prefix.DEFAULT,false);
    KubernetesClientConfigContext greenContext=new KubernetesClientConfigContext(api,greenSource,NAMESPACE,new MockEnvironment());
    KubernetesClientContextToSourceData greenData=new LabeledSecretContextToSourceDataProvider().get();
    SourceData greenSourceData=greenData.apply(greenContext);
    Assertions.assertEquals(greenSourceData.sourceData().size(),1);
    Assertions.assertEquals(greenSourceData.sourceData().get("color"),"green");
    Assertions.assertEquals(greenSourceData.sourceName(),"secret.green.default");
    String[] out=output.getAll().split("Loaded all secrets in namespace");
    Assertions.assertEquals(out.length,2);
    out=output.getAll().split("Loaded \\(from cache\\) all secrets in namespace");
    Assertions.assertEquals(out.length,2);
  }
  private void stubCall(  V1SecretList list){
    stubFor(get("/api/v1/namespaces/default/secrets").willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(list))));
  }
}

