    Assertions.assertTrue(context.isRegistered(CoreV1Api.class));
    Assertions.assertTrue(context.isRegistered(ApiClient.class));
    Assertions.assertTrue(context.isRegistered(ConfigMapConfigProperties.class));
    Assertions.assertTrue(context.isRegistered(SecretsConfigProperties.class));
    Assertions.assertTrue(context.isRegistered(ConfigMapPropertySourceLocator.class));
    Assertions.assertTrue(context.isRegistered(SecretsPropertySourceLocator.class));
    ConfigMapPropertySourceLocator configMapPropertySourceLocator=context.get(ConfigMapPropertySourceLocator.class);
    Assertions.assertSame(KubernetesClientConfigMapPropertySourceLocator.class,configMapPropertySourceLocator.getClass());
    SecretsPropertySourceLocator secretsPropertySourceLocator=context.get(SecretsPropertySourceLocator.class);
    Assertions.assertSame(KubernetesClientSecretsPropertySourceLocator.class,secretsPropertySourceLocator.getClass());
  }
  /** 
 * both ConfigMapConfigProperties and SecretsConfigProperties are enabled explicitly, as such they are both registered. It also means that ConfigMapPropertySourceLocator and SecretsPropertySourceLocator are registered too. Since retry is not enabled explicitly, we also assert the types to ensure that these are not retryable beans.
 */
  @Test void testBothPresentExplicitly(  CapturedOutput capturedOutput){
