    stubFor(get(urlMatching("/api/v1/namespaces/default/secrets.*")).inScenario(SCENARIO).whenScenarioStateIs("update").withQueryParams(WATCH_TRUE).willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(watchResponse))).willSetStateTo("add"));
    stubFor(get(urlMatching("/api/v1/namespaces/default/secrets.*")).inScenario(SCENARIO).whenScenarioStateIs("add").withQueryParams(WATCH_TRUE).willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(new Watch.Response<>(EventType.ADDED.name(),new V1Secret().metadata(new V1ObjectMeta().name("rabbit-password")).putDataItem("rabbit-pw",Base64.getEncoder().encode("password".getBytes())))))).willSetStateTo("delete"));
    stubFor(get(urlMatching("/api/v1/namespaces/default/secrets.*")).inScenario(SCENARIO).whenScenarioStateIs("delete").withQueryParams(WATCH_TRUE).willReturn(aResponse().withStatus(200).withBody(new JSON().serialize(new Watch.Response<>(EventType.DELETED.name(),new V1Secret().metadata(new V1ObjectMeta().name("rabbit-password")).putDataItem("rabbit-pw",Base64.getEncoder().encode("password".getBytes())))))).willSetStateTo("done"));
