abstract class ConfigDataSanitize {
  private static MockedStatic<KubernetesClientUtils> clientUtilsMock;
  @BeforeAll static void wireMock(){
    WireMockServer server=new WireMockServer(options().dynamicPort());
    server.start();
    WireMock.configureFor("localhost",server.port());
    clientUtilsMock=mockStatic(KubernetesClientUtils.class);
    clientUtilsMock.when(KubernetesClientUtils::kubernetesApiClient).thenReturn(new ClientBuilder().setBasePath(server.baseUrl()).build());
    clientUtilsMock.when(() -> KubernetesClientUtils.getApplicationNamespace(Mockito.any(),Mockito.any(),Mockito.any())).thenReturn("test");
    stubData();
  }
  @AfterAll static void teardown(){
    clientUtilsMock.close();
  }
}

