public class MavenWrapperDownloader {
  /** 
 * Default URL to download the maven-wrapper.jar from, if no 'downloadUrl' is provided.
 */
  private static final String DEFAULT_DOWNLOAD_URL="https://repo.maven.apache.org/maven2/io/takari/maven-wrapper/0.4.2/maven-wrapper-0.4.2.jar";
  /** 
 * Path to the maven-wrapper.properties file, which might contain a downloadUrl property to use instead of the default one.
 */
  private static final String MAVEN_WRAPPER_PROPERTIES_PATH=".mvn/wrapper/maven-wrapper.properties";
  /** 
 * Path where the maven-wrapper.jar will be saved to.
 */
  private static final String MAVEN_WRAPPER_JAR_PATH=".mvn/wrapper/maven-wrapper.jar";
  /** 
 * Name of the property which should be used to override the default download url for the wrapper.
 */
  private static final String PROPERTY_NAME_WRAPPER_URL="wrapperUrl";
  public static void main(  String args[]){
    System.out.println("- Downloader started");
    File baseDirectory=new File(args[0]);
    System.out.println("- Using base directory: " + baseDirectory.getAbsolutePath());
    File mavenWrapperPropertyFile=new File(baseDirectory,MAVEN_WRAPPER_PROPERTIES_PATH);
    String url=DEFAULT_DOWNLOAD_URL;
    if (mavenWrapperPropertyFile.exists()) {
      FileInputStream mavenWrapperPropertyFileInputStream=null;
      try {
        mavenWrapperPropertyFileInputStream=new FileInputStream(mavenWrapperPropertyFile);
        Properties mavenWrapperProperties=new Properties();
        mavenWrapperProperties.load(mavenWrapperPropertyFileInputStream);
        url=mavenWrapperProperties.getProperty(PROPERTY_NAME_WRAPPER_URL,url);
      }
 catch (      IOException e) {
        System.out.println("- ERROR loading '" + MAVEN_WRAPPER_PROPERTIES_PATH + "'");
      }
 finally {
        try {
          if (mavenWrapperPropertyFileInputStream != null) {
            mavenWrapperPropertyFileInputStream.close();
          }
        }
 catch (        IOException e) {
        }
      }
    }
    System.out.println("- Downloading from: : " + url);
    File outputFile=new File(baseDirectory.getAbsolutePath(),MAVEN_WRAPPER_JAR_PATH);
    if (!outputFile.getParentFile().exists()) {
      if (!outputFile.getParentFile().mkdirs()) {
        System.out.println("- ERROR creating output direcrory '" + outputFile.getParentFile().getAbsolutePath() + "'");
      }
    }
    System.out.println("- Downloading to: " + outputFile.getAbsolutePath());
    try {
      downloadFileFromURL(url,outputFile);
      System.out.println("Done");
      System.exit(0);
    }
 catch (    Throwable e) {
      System.out.println("- Error downloading");
      e.printStackTrace();
      System.exit(1);
    }
  }
  private static void downloadFileFromURL(  String urlString,  File destination) throws Exception {
    URL website=new URL(urlString);
    ReadableByteChannel rbc;
    rbc=Channels.newChannel(website.openStream());
    FileOutputStream fos=new FileOutputStream(destination);
    fos.getChannel().transferFrom(rbc,0,Long.MAX_VALUE);
    fos.close();
    rbc.close();
  }
}

