@SpringBootTest(properties={"spring.cloud.kubernetes.loadbalancer.mode=SERVICE","spring.main.cloud-platform=KUBERNETES","spring.cloud.kubernetes.discovery.all-namespaces=false","spring.cloud.kubernetes.client.namespace=a"},classes={LoadBalancerConfiguration.class,Configuration.class}) @ExtendWith(OutputCaptureExtension.class) class SpecificNamespaceTest {
  private static final String MY_SERVICE_URL="http://my-service";
  private static final int SERVICE_A_PORT=8888;
  private static final int SERVICE_B_PORT=8889;
  private static WireMockServer wireMockServer;
  private static WireMockServer serviceAMockServer;
  private static WireMockServer serviceBMockServer;
  private static final MockedStatic<KubernetesServiceInstanceMapper> MOCKED_STATIC=Mockito.mockStatic(KubernetesServiceInstanceMapper.class);
  @Autowired private WebClient.Builder builder;
  @Autowired private ObjectProvider<LoadBalancerClientFactory> loadBalancerClientFactory;
  @BeforeAll static void beforeAll(){
    wireMockServer=new WireMockServer(options().dynamicPort());
    wireMockServer.start();
    WireMock.configureFor("localhost",wireMockServer.port());
    serviceAMockServer=new WireMockServer(SERVICE_A_PORT);
    serviceAMockServer.start();
    WireMock.configureFor("localhost",SERVICE_A_PORT);
    serviceBMockServer=new WireMockServer(SERVICE_B_PORT);
    serviceBMockServer.start();
    WireMock.configureFor("localhost",SERVICE_B_PORT);
    MOCKED_STATIC.when(() -> KubernetesServiceInstanceMapper.createHost("my-service","a","cluster.local")).thenReturn("localhost");
    MOCKED_STATIC.when(() -> KubernetesServiceInstanceMapper.createHost("my-service","b","cluster.local")).thenReturn("localhost");
    System.setProperty(Config.KUBERNETES_MASTER_SYSTEM_PROPERTY,"http://localhost:" + wireMockServer.port());
    System.setProperty(Config.KUBERNETES_TRUST_CERT_SYSTEM_PROPERTY,"true");
    System.setProperty(Config.KUBERNETES_AUTH_TRYKUBECONFIG_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_AUTH_TRYSERVICEACCOUNT_SYSTEM_PROPERTY,"false");
    System.setProperty(Config.KUBERNETES_NAMESPACE_SYSTEM_PROPERTY,"test");
    System.setProperty(Config.KUBERNETES_HTTP2_DISABLE,"true");
  }
  @AfterAll static void afterAll(){
    wireMockServer.stop();
    serviceAMockServer.stop();
    serviceBMockServer.stop();
    MOCKED_STATIC.close();
  }
  /** 
 * <pre> - my-service is present in 'a' namespace - my-service is present in 'b' namespace - we enable search in namespace 'a' - load balancer mode is 'SERVICE' - as such, only my-service in namespace a is load balanced - we also assert the type of ServiceInstanceListSupplier corresponding to the SERVICE mode. </pre>
 */
  @Test void test(  CapturedOutput output){
    Service serviceA=Util.service("a","my-service",SERVICE_A_PORT);
    Service serviceB=Util.service("b","my-service",SERVICE_B_PORT);
    String serviceAJson=Serialization.asJson(serviceA);
    String serviceBJson=Serialization.asJson(serviceB);
    wireMockServer.stubFor(WireMock.get(WireMock.urlEqualTo("/api/v1/namespaces/a/services/my-service")).willReturn(WireMock.aResponse().withBody(serviceAJson).withStatus(200)));
    wireMockServer.stubFor(WireMock.get(WireMock.urlEqualTo("/api/v1/namespaces/b/services/my-service")).willReturn(WireMock.aResponse().withBody(serviceBJson).withStatus(200)));
    serviceAMockServer.stubFor(WireMock.get(WireMock.urlEqualTo("/")).willReturn(WireMock.aResponse().withBody("service-a-reached").withStatus(200)));
    serviceBMockServer.stubFor(WireMock.get(WireMock.urlEqualTo("/")).willReturn(WireMock.aResponse().withBody("service-b-reached").withStatus(200)));
    String serviceAResult=builder.baseUrl(MY_SERVICE_URL).build().method(HttpMethod.GET).retrieve().bodyToMono(String.class).block();
    Assertions.assertThat(serviceAResult).isEqualTo("service-a-reached");
    CachingServiceInstanceListSupplier supplier=(CachingServiceInstanceListSupplier)loadBalancerClientFactory.getIfAvailable().getProvider("my-service",ServiceInstanceListSupplier.class).getIfAvailable();
    Assertions.assertThat(supplier.getDelegate().getClass()).isSameAs(Fabric8ServicesListSupplier.class);
    Assertions.assertThat(output.getOut()).contains("serviceID : my-service");
    Assertions.assertThat(output.getOut()).contains("discovering services in namespace : a");
    wireMockServer.verify(WireMock.exactly(1),WireMock.getRequestedFor(WireMock.urlEqualTo("/api/v1/namespaces/a/services/my-service")));
    wireMockServer.verify(WireMock.exactly(0),WireMock.getRequestedFor(WireMock.urlEqualTo("/api/v1/namespaces/b/services/my-service")));
  }
}

