      Assertions.assertThat(firstCallResult).isEqualTo("service-b-reached");
      Assertions.assertThat(secondCallResult).isEqualTo("service-a-reached");
    }
    CachingServiceInstanceListSupplier supplier=(CachingServiceInstanceListSupplier)loadBalancerClientFactory.getIfAvailable().getProvider("my-service",ServiceInstanceListSupplier.class).getIfAvailable();
    Assertions.assertThat(supplier.getDelegate().getClass()).isSameAs(DiscoveryClientServiceInstanceListSupplier.class);
    wireMockServer.verify(WireMock.exactly(1),WireMock.getRequestedFor(WireMock.urlEqualTo("/api/v1/namespaces/a/services/my-service")));
    wireMockServer.verify(WireMock.exactly(1),WireMock.getRequestedFor(WireMock.urlEqualTo("/api/v1/namespaces/b/services/my-service")));
    wireMockServer.verify(WireMock.exactly(0),WireMock.getRequestedFor(WireMock.urlEqualTo("/api/v1/namespaces/c/services/my-service")));
    wireMockServer.verify(WireMock.exactly(1),WireMock.getRequestedFor(WireMock.urlEqualTo("/api/v1/namespaces/a/endpoints?fieldSelector=metadata.name%3Dmy-service")));
    wireMockServer.verify(WireMock.exactly(1),WireMock.getRequestedFor(WireMock.urlEqualTo("/api/v1/namespaces/b/endpoints?fieldSelector=metadata.name%3Dmy-service")));
    wireMockServer.verify(WireMock.exactly(0),WireMock.getRequestedFor(WireMock.urlEqualTo("/api/v1/namespaces/c/endpoints?fieldSelector=metadata.name%3Dmy-service")));
  }
}

