  /** 
 * <pre> service has no ServicePorts </pre>
 */
  @Test void testMapEmptyPorts(  CapturedOutput output){
    KubernetesLoadBalancerProperties loadBalancerProperties=new KubernetesLoadBalancerProperties();
    KubernetesDiscoveryProperties discoveryProperties=new KubernetesDiscoveryProperties(true,false,Set.of(),true,60,false,null,Set.of(),Map.of(),null,KubernetesDiscoveryProperties.Metadata.DEFAULT,0,true);
