  @SuppressWarnings("unchecked") private final NonNamespaceOperation<Service,ServiceList,ServiceResource<Service>> namespaceOperation=Mockito.mock(NonNamespaceOperation.class);
  @SuppressWarnings("unchecked") private final ServiceResource<Service> serviceResource=Mockito.mock(ServiceResource.class);
  @SuppressWarnings("unchecked") private final AnyNamespaceOperation<Service,ServiceList,ServiceResource<Service>> multiDeletable=Mockito.mock(AnyNamespaceOperation.class);
  @Test void testPositiveMatch(){
    when(mapper.map(any(Service.class))).thenReturn(new DefaultKubernetesServiceInstance("","","",0,null,false));
    when(this.client.getNamespace()).thenReturn("test");
    when(this.client.services()).thenReturn(this.serviceOperation);
    when(this.serviceOperation.inNamespace("test")).thenReturn(namespaceOperation);
    when(this.namespaceOperation.withName("test-service")).thenReturn(this.serviceResource);
    when(this.serviceResource.get()).thenReturn(buildService("test-service",8080));
    KubernetesServicesListSupplier<Service> supplier=new Fabric8ServicesListSupplier(environment,client,mapper,KubernetesDiscoveryProperties.DEFAULT);
    List<ServiceInstance> instances=supplier.get().blockFirst();
    Assertions.assertEquals(1,instances.size());
  }
  @Test void testPositiveMatchAllNamespaces(){
    when(mapper.map(any(Service.class))).thenReturn(new DefaultKubernetesServiceInstance("","","",0,null,false));
    when(this.client.services()).thenReturn(this.serviceOperation);
    when(this.serviceOperation.inAnyNamespace()).thenReturn(this.multiDeletable);
    when(this.multiDeletable.withField("metadata.name","test-service")).thenReturn(this.multiDeletable);
    ServiceList serviceList=new ServiceList();
    serviceList.getItems().add(buildService("test-service",8080));
    when(this.multiDeletable.list()).thenReturn(serviceList);
    KubernetesDiscoveryProperties discoveryProperties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,null,Set.of(),Map.of(),null,KubernetesDiscoveryProperties.Metadata.DEFAULT,0,false);
    KubernetesServicesListSupplier<Service> supplier=new Fabric8ServicesListSupplier(environment,client,mapper,discoveryProperties);
    List<ServiceInstance> instances=supplier.get().blockFirst();
    Assertions.assertEquals(1,instances.size());
  }
  private Service buildService(  String name,  int port){
    return new ServiceBuilder().withNewMetadata().withName(name).endMetadata().withNewSpec().addNewPort().withPort(port).endPort().endSpec().build();
  }
}

