@Configuration(proxyBeanMethods=false) @ConditionalOnCloudPlatform(CloudPlatform.KUBERNETES) @ConditionalOnClass(name="org.springframework.boot.actuate.endpoint.SanitizableData") @ConditionalOnSanitizeSecrets class KubernetesCommonsSanitizeAutoConfiguration {
  @Bean @ConditionalOnMissingBean SanitizingFunction secretsPropertySourceSanitizingFunction(){
    return data -> {
      PropertySource<?> propertySource=data.getPropertySource();
      if (propertySource instanceof BootstrapPropertySource<?>) {
        PropertySource<?> source=bootstrapPropertySource.getDelegate();
        if (source instanceof SecretsPropertySource) {
          return new SanitizableData(propertySource,data.getKey(),data.getValue()).withValue(SanitizableData.SANITIZED_VALUE);
        }
      }
      if (propertySource instanceof SecretsPropertySource) {
        return new SanitizableData(propertySource,data.getKey(),data.getValue()).withValue(SanitizableData.SANITIZED_VALUE);
      }
      if (propertySource instanceof CompositePropertySource) {
        Collection<PropertySource<?>> sources=compositePropertySource.getPropertySources();
        for (        PropertySource<?> one : sources) {
          if (one.containsProperty(data.getKey()) && one instanceof SecretsPropertySource) {
            return new SanitizableData(propertySource,data.getKey(),data.getValue()).withValue(SanitizableData.SANITIZED_VALUE);
          }
        }
      }
      return data;
    }
;
  }
}

