/** 
 * Utility class that works with configuration properties.
 * @author Ioannis Canellos
 */
public final class ConfigUtils {
  private static final Log LOG=LogFactory.getLog(ConfigUtils.class);
  private static final BiPredicate<String,String> ENDS_WITH_PROFILE_AND_EXTENSION=(sourceName,activeProfile) -> sourceName.endsWith("-" + activeProfile + ".yml") || sourceName.endsWith("-" + activeProfile + ".yaml") || sourceName.endsWith("-" + activeProfile + ".properties");
  private ConfigUtils(){
  }
  public static String getApplicationName(  Environment env,  String configName,  String configurationTarget){
    if (!StringUtils.hasLength(configName)) {
      LOG.debug(configurationTarget + " name has not been set, taking it from property/env " + SPRING_APPLICATION_NAME+ " (default="+ FALLBACK_APPLICATION_NAME+ ")");
      configName=env.getProperty(SPRING_APPLICATION_NAME,FALLBACK_APPLICATION_NAME);
    }
    return configName;
  }
  /** 
 * @param explicitPrefix value of'spring.cloud.kubernetes.config|secrets.sources.explicitPrefix'
 * @param useNameAsPrefix value of'spring.cloud.kubernetes.config|secrets.sources.useNameAsPrefix'
 * @param defaultUseNameAsPrefix value of'spring.cloud.kubernetes.config|secrets.defaultUseNameAsPrefix'
 * @param normalizedName either the name of'spring.cloud.kubernetes.config|secrets.sources.name' or 'spring.cloud.kubernetes.config|secrets.name'
 * @return prefix to use in normalized sources
 */
  public static Prefix findPrefix(  String explicitPrefix,  Boolean useNameAsPrefix,  boolean defaultUseNameAsPrefix,  String normalizedName){
    if (StringUtils.hasText(explicitPrefix)) {
      Prefix.computeKnown(() -> explicitPrefix);
      return Prefix.KNOWN;
    }
    if (useNameAsPrefix != null) {
      if (useNameAsPrefix) {
        if (normalizedName == null) {
          return Prefix.DELAYED;
        }
        Prefix.computeKnown(() -> normalizedName);
        return Prefix.KNOWN;
      }
      return Prefix.DEFAULT;
    }
    if (defaultUseNameAsPrefix) {
      if (normalizedName == null) {
        return Prefix.DELAYED;
      }
      Prefix.computeKnown(() -> normalizedName);
      return Prefix.KNOWN;
    }
    return Prefix.DEFAULT;
  }
  /** 
 * @param defaultIncludeProfileSpecificSources value of'spring.cloud.kubernetes.config.includeProfileSpecificSources'
 * @param includeProfileSpecificSources value of'spring.cloud.kubernetes.config.sources.includeProfileSpecificSources'
 * @return useProfileNameAsPrefix to be used in normalized sources
 */
  public static boolean includeProfileSpecificSources(  boolean defaultIncludeProfileSpecificSources,  Boolean includeProfileSpecificSources){
    if (includeProfileSpecificSources != null) {
      return includeProfileSpecificSources;
    }
    return defaultIncludeProfileSpecificSources;
  }
  /** 
 * action to take when an Exception happens when dealing with a source.
 */
  public static void onException(  boolean failFast,  Exception e){
    if (failFast) {
      throw new IllegalStateException(e.getMessage(),e);
    }
    LOG.warn(e.getMessage() + ". Ignoring.",e);
  }
  public static SourceData withPrefix(  String target,  PrefixContext context){
    Map<String,Object> withPrefix=CollectionUtils.newHashMap(context.data().size());
    context.data().forEach((key,value) -> withPrefix.put(context.prefix() + "." + key,value));
    String propertySourceTokens=String.join(PROPERTY_SOURCE_NAME_SEPARATOR,context.propertySourceNames().stream().sorted().collect(Collectors.toCollection(LinkedHashSet::new)));
    return new SourceData(sourceName(target,propertySourceTokens,context.namespace()),withPrefix);
  }
  public static String sourceName(  String target,  String applicationName,  String namespace){
    return target + PROPERTY_SOURCE_NAME_SEPARATOR + applicationName+ PROPERTY_SOURCE_NAME_SEPARATOR+ namespace;
  }
  public static String sourceName(  String target,  String applicationName,  String namespace,  String[] profiles){
    String name=sourceName(target,applicationName,namespace);
    if (profiles != null && profiles.length > 0) {
      name=name + PROPERTY_SOURCE_NAME_SEPARATOR + StringUtils.arrayToDelimitedString(profiles,"-");
    }
    return name;
  }
  public static MultipleSourcesContainer processNamedData(  List<StrippedSourceContainer> strippedSources,  Environment environment,  LinkedHashSet<String> sourceNames,  String namespace,  boolean decode){
    return processNamedData(strippedSources,environment,sourceNames,namespace,decode,true);
  }
  /** 
 * transforms raw data from one or multiple sources into an entry of source names and flattened data that they all hold (potentially overriding entries without any defined order).
 */
  public static MultipleSourcesContainer processNamedData(  List<StrippedSourceContainer> strippedSources,  Environment environment,  LinkedHashSet<String> sourceNames,  String namespace,  boolean decode,  boolean includeDefaultProfileData){
    Map<String,StrippedSourceContainer> hashByName=strippedSources.stream().collect(Collectors.toMap(StrippedSourceContainer::name,Function.identity()));
    LinkedHashSet<String> foundSourceNames=new LinkedHashSet<>();
    Map<String,Object> data=new HashMap<>();
    sourceNames.forEach(sourceName -> {
      StrippedSourceContainer stripped=hashByName.get(sourceName);
      if (stripped != null) {
        LOG.debug("Found source with name : '" + sourceName + " in namespace: '"+ namespace+ "'");
        foundSourceNames.add(sourceName);
        Map<String,String> rawData=stripped.data();
        if (decode) {
          rawData=decodeData(rawData);
        }
        if (processSource(includeDefaultProfileData,environment,sourceName,rawData)) {
          data.putAll(SourceDataEntriesProcessor.processAllEntries(rawData == null ? Map.of() : rawData,environment,includeDefaultProfileData));
        }
      }
    }
);
    return new MultipleSourcesContainer(foundSourceNames,data);
  }
  static boolean processSource(  boolean includeDefaultProfileData,  Environment environment,  String sourceName,  Map<String,String> sourceRawData){
    List<String> activeProfiles=Arrays.stream(environment.getActiveProfiles()).toList();
    boolean emptyActiveProfiles=activeProfiles.isEmpty();
    boolean profileBasedSourceName=activeProfiles.stream().anyMatch(activeProfile -> sourceName.endsWith("-" + activeProfile));
    boolean defaultProfilePresent=activeProfiles.contains("default");
    return includeDefaultProfileData || emptyActiveProfiles || profileBasedSourceName|| defaultProfilePresent|| rawDataContainsProfileBasedSource(activeProfiles,sourceRawData).getAsBoolean();
  }
  static BooleanSupplier rawDataContainsProfileBasedSource(  List<String> activeProfiles,  Map<String,String> sourceRawData){
    return () -> Optional.ofNullable(sourceRawData).orElse(Map.of()).keySet().stream().anyMatch(keyName -> activeProfiles.stream().anyMatch(activeProfile -> ENDS_WITH_PROFILE_AND_EXTENSION.test(keyName,activeProfile)));
  }
  /** 
 * transforms raw data from one or multiple sources into an entry of source names and flattened data that they all hold (potentially overriding entries without any defined order). This method first searches by labels, find the sources, then uses these names to find any profile based sources.
 */
  public static MultipleSourcesContainer processLabeledData(  List<StrippedSourceContainer> containers,  Environment environment,  Map<String,String> labels,  String namespace,  Set<String> profiles,  boolean decode){
    List<StrippedSourceContainer> byLabels=containers.stream().filter(one -> {
      Map<String,String> sourceLabels=one.labels();
      Map<String,String> labelsToSearchAgainst=sourceLabels == null ? Map.of() : sourceLabels;
      return labelsToSearchAgainst.entrySet().containsAll((labels.entrySet()));
    }
).toList();
    List<String> sourceNamesByLabelsWithProfile=new ArrayList<>();
    if (profiles != null && !profiles.isEmpty()) {
      for (      StrippedSourceContainer one : byLabels) {
        for (        String profile : profiles) {
          String name=one.name() + "-" + profile;
          sourceNamesByLabelsWithProfile.add(name);
        }
      }
    }
    List<StrippedSourceContainer> byProfile=containers.stream().filter(one -> sourceNamesByLabelsWithProfile.contains(one.name())).toList();
    List<StrippedSourceContainer> all=new ArrayList<>(byLabels.size() + byProfile.size());
    all.addAll(byLabels);
    all.addAll(byProfile);
    LinkedHashSet<String> sourceNames=new LinkedHashSet<>();
    Map<String,Object> result=new HashMap<>();
    all.forEach(source -> {
      String foundSourceName=source.name();
      LOG.debug("Loaded source with name : '" + foundSourceName + " in namespace: '"+ namespace+ "'");
      sourceNames.add(foundSourceName);
      Map<String,String> rawData=source.data();
      if (decode) {
        rawData=decodeData(rawData);
      }
      result.putAll(SourceDataEntriesProcessor.processAllEntries(rawData,environment));
    }
);
    return new MultipleSourcesContainer(sourceNames,result);
  }
  private static Map<String,String> decodeData(  Map<String,String> data){
    Map<String,String> result=new HashMap<>(CollectionUtils.newHashMap(data.size()));
    data.forEach((key,value) -> result.put(key,new String(Base64.getDecoder().decode(value)).trim()));
    return result;
  }
  public static <T>void registerSingle(  ConfigurableBootstrapContext bootstrapContext,  Class<T> cls,  T instance,  String name){
    bootstrapContext.registerIfAbsent(cls,BootstrapRegistry.InstanceSupplier.of(instance));
    bootstrapContext.addCloseListener(event -> {
      if (event.getApplicationContext().getBeanFactory().getSingleton(name) == null) {
        event.getApplicationContext().getBeanFactory().registerSingleton(name,event.getBootstrapContext().get(cls));
      }
    }
);
  }
  /** 
 * append prefix to the keys and return a new Map with the new values.
 */
  public static Map<String,String> keysWithPrefix(  Map<String,String> map,  String prefix){
    if (map == null || map.isEmpty()) {
      return Map.of();
    }
    if (!StringUtils.hasText(prefix)) {
      return map;
    }
    Map<String,String> result=CollectionUtils.newHashMap(map.size());
    map.forEach((key,value) -> result.put(prefix + key,value));
    return result;
  }
public static final class Prefix {
    /** 
 * prefix has not been provided.
 */
    public static final Prefix DEFAULT=new Prefix(() -> "","DEFAULT");
    /** 
 * prefix has been enabled, but the actual value will be known later; the value for the prefix will be the name of the source. (this is the case for a prefix-enabled labeled source for example)
 */
    public static final Prefix DELAYED=new Prefix(() -> {
      throw new IllegalArgumentException("prefix is delayed, needs to be taken elsewhere");
    }
,"DELAYED");
    /** 
 * prefix is known at the callsite.
 */
    public static Prefix KNOWN;
    public Supplier<String> prefixProvider(){
      return prefixProvider;
    }
    private final Supplier<String> prefixProvider;
    private final String name;
    private Prefix(    Supplier<String> prefixProvider,    String name){
      this.prefixProvider=prefixProvider;
      this.name=name;
    }
    private static void computeKnown(    Supplier<String> supplier){
      KNOWN=new Prefix(supplier,"KNOWN");
    }
    public String toString(){
      return new ToStringCreator(this).append("name",name).toString();
    }
  }
}

