@ExtendWith(MockitoExtension.class) class LazilyInstantiateTest {
  private static final String TAG="excluded-from-before";
  private static final String SINGLETON="singleton";
  @Mock private Supplier<String> mockSupplier;
  @BeforeEach public void setUp(  TestInfo testInfo) throws Exception {
    if (!testInfo.getTags().contains(TAG)) {
      when(this.mockSupplier.get()).thenReturn(SINGLETON).thenThrow(new RuntimeException("Supplier was called more than once!"));
    }
  }
  @Tag(TAG) @Test void supplierNotCalledInLazyInstantiateFactoryMethod(){
    LazilyInstantiate.using(this.mockSupplier);
    verifyNoInteractions(this.mockSupplier);
  }
  @Test void factoryReturnsSingletonFromSupplier(){
    LazilyInstantiate<String> lazyStringFactory=LazilyInstantiate.using(this.mockSupplier);
    String singletonString=lazyStringFactory.get();
    assertThat(singletonString).isEqualTo(SINGLETON);
  }
  @Test void factoryOnlyCallsSupplierOnce(){
    LazilyInstantiate<String> lazyStringFactory=LazilyInstantiate.using(this.mockSupplier);
    lazyStringFactory.get();
    lazyStringFactory.get();
  }
}

