class SanitizeTests {
  private static final boolean SHOW_UNSANITIZED=true;
  private static final List<SanitizingFunction> SANITIZING_FUNCTIONS=List.of(new KubernetesCommonsSanitizeAutoConfiguration().secretsPropertySourceSanitizingFunction());
  @Test void bootstrapPropertySourceNotSecrets(){
    BootstrapPropertySource<?> bootstrapPropertySource=new BootstrapPropertySource<>(new EnumerablePropertySource<>("enumerable"){
      @Override public String[] getPropertyNames(){
        return new String[0];
      }
      @Override public Object getProperty(      String name){
        return null;
      }
    }
);
    Sanitizer sanitizer=new Sanitizer(SANITIZING_FUNCTIONS);
    SanitizableData sanitizableData=new SanitizableData(bootstrapPropertySource,"secret","xyz");
    Assertions.assertEquals(sanitizer.sanitize(sanitizableData,SHOW_UNSANITIZED),"xyz");
  }
  @Test void bootstrapPropertySourceSecrets(){
    BootstrapPropertySource<?> bootstrapPropertySource=new BootstrapPropertySource<>(new SecretsPropertySource(new SourceData("secret-source",Map.of())));
    Sanitizer sanitizer=new Sanitizer(SANITIZING_FUNCTIONS);
    SanitizableData sanitizableData=new SanitizableData(bootstrapPropertySource,"secret","xyz");
    Assertions.assertEquals(sanitizer.sanitize(sanitizableData,SHOW_UNSANITIZED),SANITIZED_VALUE);
  }
  @Test void notSecretsPropertySource(){
    BootstrapPropertySource<?> bootstrapPropertySource=new BootstrapPropertySource<>(new MountConfigMapPropertySource("mount-source",Map.of()));
    Sanitizer sanitizer=new Sanitizer(SANITIZING_FUNCTIONS);
    SanitizableData sanitizableData=new SanitizableData(bootstrapPropertySource,"secret","xyz");
    Assertions.assertEquals(sanitizer.sanitize(sanitizableData,SHOW_UNSANITIZED),"xyz");
  }
  @Test void secretsPropertySource(){
    BootstrapPropertySource<?> bootstrapPropertySource=new BootstrapPropertySource<>(new SecretsPropertySource(new SourceData("secret-source",Map.of())));
    Sanitizer sanitizer=new Sanitizer(SANITIZING_FUNCTIONS);
    SanitizableData sanitizableData=new SanitizableData(bootstrapPropertySource,"secret","xyz");
    Assertions.assertEquals(sanitizer.sanitize(sanitizableData,SHOW_UNSANITIZED),SANITIZED_VALUE);
  }
  @Test void compositeOneSecretOneMount(){
    CompositePropertySource compositePropertySource=new CompositePropertySource("composite");
    compositePropertySource.addFirstPropertySource(new SecretsPropertySource(new SourceData("secret-source",Map.of("secret","xyz"))));
    compositePropertySource.addFirstPropertySource(new MountConfigMapPropertySource("mount-source",Map.of("mount","abc")));
    Sanitizer sanitizer=new Sanitizer(SANITIZING_FUNCTIONS);
    SanitizableData sanitizableDataSecret=new SanitizableData(compositePropertySource,"secret","xyz");
    SanitizableData sanitizableDataMount=new SanitizableData(compositePropertySource,"mount","abc");
    Assertions.assertEquals(sanitizer.sanitize(sanitizableDataSecret,SHOW_UNSANITIZED),SANITIZED_VALUE);
    Assertions.assertEquals(sanitizer.sanitize(sanitizableDataMount,SHOW_UNSANITIZED),"abc");
  }
}

