/** 
 * @author wind57Tests the {@link org.springframework.cloud.kubernetes.commons.discovery.KubernetesDiscoveryClientHealthIndicatorInitializer}with the fabric8 client.
 */
class KubernetesDiscoveryClientHealthIndicatorInitializerTests {
  private static ApplicationEventPublisher publisher;
  private static final Object POD=Mockito.mock(Object.class);
  @AfterEach void afterEach(){
    Mockito.reset(publisher,POD);
  }
  @Test @SuppressWarnings("unchecked") void testInstanceRegistrationEventPublishedWhenInsideK8s(){
    new ApplicationContextRunner().withUserConfiguration(InstanceRegistrationEventPublishedInsideK8sConfiguration.class).run(context -> assertThat(context).hasSingleBean(PodUtils.class));
    ArgumentCaptor<InstanceRegisteredEvent<RegisteredEventSource>> captor=ArgumentCaptor.forClass(InstanceRegisteredEvent.class);
    Mockito.verify(publisher,Mockito.times(1)).publishEvent(captor.capture());
    KubernetesDiscoveryClientHealthIndicatorInitializer.RegisteredEventSource source=(KubernetesDiscoveryClientHealthIndicatorInitializer.RegisteredEventSource)captor.getValue().getSource();
    assertThat(source.cloudPlatform()).isEqualTo("kubernetes");
    assertThat(source.inside()).isTrue();
    assertThat(source.pod()).isSameAs(POD);
  }
  @Test @SuppressWarnings("unchecked") void testInstanceRegistrationEventPublishedWhenOutsideK8s(){
    new ApplicationContextRunner().withUserConfiguration(InstanceRegistrationEventPublishedOutsideK8sConfiguration.class).run(context -> assertThat(context).hasSingleBean(PodUtils.class));
    ArgumentCaptor<InstanceRegisteredEvent<RegisteredEventSource>> captor=ArgumentCaptor.forClass(InstanceRegisteredEvent.class);
    Mockito.verify(publisher,Mockito.times(1)).publishEvent(captor.capture());
    KubernetesDiscoveryClientHealthIndicatorInitializer.RegisteredEventSource source=(KubernetesDiscoveryClientHealthIndicatorInitializer.RegisteredEventSource)captor.getValue().getSource();
    assertThat(source.cloudPlatform()).isEqualTo("kubernetes");
    assertThat(source.inside()).isFalse();
    assertThat(source.pod()).isNotNull();
  }
@Configuration static class InstanceRegistrationEventPublishedInsideK8sConfiguration {
    @Bean @SuppressWarnings("unchecked") PodUtils<Object> podUtils(){
      PodUtils<Object> podUtils=Mockito.mock(PodUtils.class);
      Mockito.when(podUtils.isInsideKubernetes()).thenReturn(true);
      Mockito.when(podUtils.currentPod()).thenReturn(() -> POD);
      return podUtils;
    }
    @Bean @Primary ApplicationEventPublisher publisher(){
      publisher=Mockito.mock(ApplicationEventPublisher.class);
      return publisher;
    }
    @Bean KubernetesDiscoveryClientHealthIndicatorInitializer indicatorInitializer(    PodUtils<Object> podUtils,    ApplicationEventPublisher publisher){
      return new KubernetesDiscoveryClientHealthIndicatorInitializer(podUtils,publisher);
    }
  }
@Configuration static class InstanceRegistrationEventPublishedOutsideK8sConfiguration {
    @Bean @SuppressWarnings("unchecked") PodUtils<Object> podUtils(){
