/** 
 * @author wind57
 */
public class KubernetesProfileEnvironmentPostProcessorTest {
  private static final String FOUNT_IT="foundIt";
  private static final String PATH="/some/path";
  private MockedStatic<Paths> paths;
  private MockedStatic<Files> files;
  private final SpringApplication springApplication=Mockito.mock(SpringApplication.class);
  private final ConfigurableApplicationContext context=new AnnotationConfigApplicationContext();
  private static final AbstractKubernetesProfileEnvironmentPostProcessor POST_PROCESSOR_INSIDE=new AbstractKubernetesProfileEnvironmentPostProcessor(){
    @Override protected boolean isInsideKubernetes(    Environment environment){
      return true;
    }
  }
;
  private static final AbstractKubernetesProfileEnvironmentPostProcessor POST_PROCESSOR_OUTSIDE=new AbstractKubernetesProfileEnvironmentPostProcessor(){
    @Override protected boolean isInsideKubernetes(    Environment environment){
      return false;
    }
  }
;
  @BeforeEach public void before(){
    paths=Mockito.mockStatic(Paths.class);
    files=Mockito.mockStatic(Files.class);
  }
  @AfterEach public void after(){
    paths.close();
    files.close();
  }
  /** 
 * <pre> 1) not running on kubernetes </pre>
 */
  @Test public void testKubernetesDisabled(){
    TestPropertySourceUtils.addInlinedPropertiesToEnvironment(context);
    POST_PROCESSOR_INSIDE.postProcessEnvironment(context.getEnvironment(),springApplication);
    assertKubernetesProfileNotPresent();
    assertKubernetesPropertySourceNotPresent();
  }
  /** 
 * <pre> 1) "spring.main.cloud-platform=KUBERNETES" 2) "spring.cloud.kubernetes.client.serviceAccountNamespacePath" is present, but does not resolve to an actual File </pre>
 */
  @Test public void testKubernetesEnabledAndServiceAccountNamespacePathIsNotResolved(){
    TestPropertySourceUtils.addInlinedPropertiesToEnvironment(context,"spring.main.cloud-platform=KUBERNETES","spring.cloud.kubernetes.client.serviceAccountNamespacePath=" + PATH);
    serviceAccountFileResolved(false,PATH);
    POST_PROCESSOR_INSIDE.postProcessEnvironment(context.getEnvironment(),springApplication);
    assertKubernetesProfilePresent();
    assertKubernetesPropertySourceNotPresent();
  }
  /** 
 * <pre> 1) "spring.main.cloud-platform=KUBERNETES" 2) "spring.cloud.kubernetes.client.serviceAccountNamespacePath" is present and resolves to an actual File </pre>
 */
  @Test public void testKubernetesEnabledAndServiceAccountNamespacePathIsResolved(){
    TestPropertySourceUtils.addInlinedPropertiesToEnvironment(context,"spring.main.cloud-platform=KUBERNETES","spring.cloud.kubernetes.client.serviceAccountNamespacePath=" + PATH);
    Path path=serviceAccountFileResolved(true,PATH);
    mockServiceAccountNamespace(path);
    POST_PROCESSOR_INSIDE.postProcessEnvironment(context.getEnvironment(),springApplication);
    assertKubernetesProfilePresent();
    assertKubernetesPropertySourcePresent();
  }
  /** 
 * <pre> 1) "spring.main.cloud-platform=KUBERNETES" 2) "spring.cloud.kubernetes.client.serviceAccountNamespacePath" is not present, as such: 3) "/var/run/secrets/kubernetes.io/serviceaccount/namespace" is picked up, which is resolved and present </pre>
 */
  @Test public void testKubernetesEnabledAndServiceAccountNamespacePathIsResolvedViaDefaultLocation(){
    TestPropertySourceUtils.addInlinedPropertiesToEnvironment(context,"spring.main.cloud-platform=KUBERNETES");
    Path path=serviceAccountFileResolved(true,"/var/run/secrets/kubernetes.io/serviceaccount/namespace");
    mockServiceAccountNamespace(path);
    POST_PROCESSOR_INSIDE.postProcessEnvironment(context.getEnvironment(),springApplication);
    assertKubernetesProfilePresent();
    assertKubernetesPropertySourcePresent();
  }
  /** 
 * <pre> 1) "spring.main.cloud-platform=KUBERNETES" 2) isInsideKubernetes returns false 3) "spring.cloud.kubernetes.client.serviceAccountNamespacePath" is not present, as such: 4) "/var/run/secrets/kubernetes.io/serviceaccount/namespace" is picked up, which is resolved and present </pre>
 */
  @Test public void testOutsideKubernetes(){
    TestPropertySourceUtils.addInlinedPropertiesToEnvironment(context,"spring.main.cloud-platform=KUBERNETES");
    Path path=serviceAccountFileResolved(true,"/var/run/secrets/kubernetes.io/serviceaccount/namespace");
    mockServiceAccountNamespace(path);
    POST_PROCESSOR_OUTSIDE.postProcessEnvironment(context.getEnvironment(),springApplication);
    assertKubernetesProfileNotPresent();
    assertKubernetesPropertySourcePresent();
  }
  private void assertKubernetesProfileNotPresent(){
    Assertions.assertFalse(kubernetesProfile().isPresent(),"'kubernetes' profile must not be present when not running in kubernetes");
  }
  private void assertKubernetesProfilePresent(){
    Assertions.assertTrue(kubernetesProfile().isPresent(),"'kubernetes' profile must be present when running in kubernetes");
  }
  private void assertKubernetesPropertySourceNotPresent(){
    Optional<PropertySource<?>> kubernetesPropertySource=kubernetesPropertySource();
    Assertions.assertFalse(kubernetesPropertySource.isPresent(),"'KUBERNETES_NAMESPACE_PROPERTY_SOURCE' source must not be present when not running on kubernetes");
  }
  private void assertKubernetesPropertySourcePresent(){
    Optional<PropertySource<?>> kubernetesPropertySource=kubernetesPropertySource();
    Assertions.assertTrue(kubernetesPropertySource.isPresent(),"'KUBERNETES_NAMESPACE_PROPERTY_SOURCE' source must be present when running on kubernetes");
    String property=(String)kubernetesPropertySource.get().getProperty("spring.cloud.kubernetes.client.namespace");
    Assertions.assertEquals(property,FOUNT_IT,"'spring.cloud.kubernetes.client.namespace' must be set to 'foundIt'");
  }
  /** 
 * <pre> 1) serviceAccountNamespace File is present or not 2) if the above is present, under what actualPath </pre>
 */
  private Path serviceAccountFileResolved(  boolean present,  String actualPath){
    Path path=Mockito.mock(Path.class);
    paths.when(() -> Paths.get(actualPath)).thenReturn(path);
    files.when(() -> Files.isRegularFile(path)).thenReturn(present);
    return path;
  }
  private void mockServiceAccountNamespace(  Path path){
    files.when(() -> Files.readAllBytes(path)).thenReturn(FOUNT_IT.getBytes());
  }
  private Optional<String> kubernetesProfile(){
    return Arrays.stream(context.getEnvironment().getActiveProfiles()).filter("kubernetes"::equals).findFirst();
  }
  private Optional<PropertySource<?>> kubernetesPropertySource(){
    return context.getEnvironment().getPropertySources().stream().filter(x -> "KUBERNETES_NAMESPACE_PROPERTY_SOURCE".equals(x.getName())).findAny();
  }
}

