    String namespace="namespace-a";
    boolean decode=false;
    boolean includeDefaultProfileData=false;
    MultipleSourcesContainer result=ConfigUtils.processNamedData(strippedSources,environment,sourceNames,namespace,decode,includeDefaultProfileData);
    Assertions.assertNotNull(result);
    Assertions.assertEquals(result.names().toString(),"[account]");
    Assertions.assertEquals(result.data(),Map.of("one","1111","four","4"));
    Assertions.assertTrue(output.getOut().contains("entry : account.properties will be skipped"));
    Assertions.assertTrue(output.getOut().contains("entry : account-default.properties will be skipped"));
  }
  /** 
 * <pre> - includeDefaultProfileData         = false - emptyActiveProfiles               = false - profileBasedSourceName            = true - defaultProfilePresent             = does not matter - rawDataContainsProfileBasedSource = does not matter </pre>
 */
  @Test void testProcessSourceSix(){
    boolean includeDefaultProfileData=false;
    MockEnvironment environment=new MockEnvironment();
    environment.setActiveProfiles("k8s");
    String sourceName="account-k8s";
    Map<String,String> sourceRawData=Map.of("one","1","account-k8s.properties","one=11");
    boolean result=ConfigUtils.processSource(includeDefaultProfileData,environment,sourceName,sourceRawData);
    Assertions.assertTrue(result);
  }
  /** 
 * <pre> - request is coming from config server - activeProfile = ['k8s'] - sourceName = 'account-k8s' </pre> As such the above will generate: <pre> - includeDefaultProfileData         = false - emptyActiveProfiles               = false - profileBasedSourceName            = true - defaultProfilePresent             = does not matter - rawDataContainsProfileBasedSource = does not matter </pre> In this case, a few types of data will be read: <pre> - all simple properties - all nested ones (yaml/yml/properties themselves) that match "${SOURCE_NAME}.{EXTENSION}" (in our case 'account.properties') - all nested ones (yaml/yml/properties themselves) that match "${SOURCE_NAME}-${ACTIVE_PROFILE}.{EXTENSION}" (in our case 'account-k8s.properties') - there are strict sorting rules if both of the above are matched </pre>
 */
  @Test void testProcessNamedDataSix(  CapturedOutput output){
    Map<String,String> sourceRawData=Map.of("one","1","two","2","account.properties","one=11\nthree=3","account-default.properties","one=111\nfour=4","account-k8s.properties","one=1111\nfive=5","account-prod.properties","six=6");
    String sourceName="account-k8s";
    List<StrippedSourceContainer> strippedSources=List.of(new StrippedSourceContainer(Map.of(),sourceName,sourceRawData));
    MockEnvironment environment=new MockEnvironment().withProperty("spring.application.name","account");
    environment.setActiveProfiles("k8s");
    LinkedHashSet<String> sourceNames=new LinkedHashSet<>(List.of(sourceName));
    String namespace="namespace-a";
    boolean decode=false;
    boolean includeDefaultProfileData=false;
    MultipleSourcesContainer result=ConfigUtils.processNamedData(strippedSources,environment,sourceNames,namespace,decode,includeDefaultProfileData);
    Assertions.assertNotNull(result);
    Assertions.assertEquals(result.names().toString(),"[account-k8s]");
    Assertions.assertEquals(result.data(),Map.of("one","1111","five","5"));
    Assertions.assertTrue(output.getOut().contains("entry : account-prod.properties will be skipped"));
    Assertions.assertTrue(output.getOut().contains("entry : account.properties will be skipped"));
    Assertions.assertTrue(output.getOut().contains("entry : account-default.properties will be skipped"));
  }
}

